import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, addDoc, updateDoc, deleteDoc, onSnapshot, collection, query, where, arrayUnion, serverTimestamp, getDocs } from 'firebase/firestore';

// Firebase configuration from environment variables
const firebaseConfig = JSON.parse(process.env.REACT_APP_FIREBASE_CONFIG || '{}');
const appId = process.env.REACT_APP_ID || 'default-app-id';

// Initialize Firebase outside the component to avoid re-initialization
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// Helper for generating unique IDs (not cryptographically secure, but fine for game items)
const generateId = () => Math.random().toString(36).substring(2, 11);

// Pre-defined game data
const PROMPT_CARDS = {
    "First Dates": [
        { type: "fill", pillar: "Boundaries", text: "My date set a clear boundary by stating they wouldn't discuss anything before __________________." },
        { type: "fill", pillar: "Communication", text: "I knew there would be communication challenges when their opening line was, 'So, do you believe in __________________?'" },
        { type: "fill", pillar: "Respect", text: "My date showed a shocking lack of respect when they started __________________ mid-conversation." },
        { type: "thisSoundsLike", pillar: "Compromise", text: "This sounds like... someone who, on a first date, orders for both of you and then says, 'No compromise on the appetizers!'" },
        { type: "fill", pillar: "Boundaries", text: "The biggest red flag on our first date was when they instantly started talking about __________________." },
        { type: "fill", pillar: "Communication", text: "Their idea of 'getting to know you' involved asking about my __________________." },
        { type: "fill", pillar: "Respect", text: "I lost all respect for them when they pulled out a __________________ at the table." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... someone who brings their ex to a first date as a 'wingman'." },
    ],
    "Proposals & Engagements": [
        { type: "fill", pillar: "Compromise", text: "When I tried to compromise on the wedding theme, they firmly stated, 'It's either __________________ or nothing!'" },
        { type: "fill", pillar: "Communication", text: "They proposed using an elaborate code only they understood, which made for terrible __________________." },
        { type: "fill", pillar: "Respect", text: "My partner showed their true respect for my wishes by including __________________ in the engagement announcement." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... someone whose engagement photo shoot involved breaking into a national park for 'the perfect shot,' ignoring all signs." },
        { type: "fill", pillar: "Compromise", text: "Our engagement almost ended when we couldn't compromise on __________________." },
        { type: "fill", pillar: "Communication", text: "The proposal would have been perfect if only their communication hadn't involved __________________." },
        { type: "fill", pillar: "Respect", text: "I knew they respected me when their proposal included a promise to never __________________." },
        { type: "thisSoundsLike", pillar: "Communication", text: "This sounds like... a proposal delivered via carrier pigeon, with a misspelled 'marry' note." },
    ],
    "Family & Children": [
        { type: "fill", pillar: "Communication", text: "My partner and I communicate solely through __________________ when discussing bedtime routines." },
        { type: "fill", pillar: "Boundaries", text: "My in-laws always cross the boundary by __________________ every time they visit." },
        { type: "fill", pillar: "Respect", text: "Our toddler shows respect for household rules by __________________ at bedtime." },
        { type: "thisSoundsLike", pillar: "Compromise", text: "This sounds like... someone who, during a family road trip, insists on listening to their obscure polka music playlist for eight hours straight with no compromise." },
        { type: "fill", pillar: "Communication", text: "Family dinners often devolve into chaos when someone mentions __________________." },
        { type: "fill", pillar: "Boundaries", text: "We had to set a strict boundary about __________________ after the last family gathering." },
        { type: "fill", pillar: "Respect", text: "Our kids finally learned respect when we introduced the rule about __________________." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... parents who track their adult children's every move using a GPS chip." },
    ],
    "Friends with Benefits (FWB)": [
        { type: "fill", pillar: "Boundaries", text: "Our FWB arrangement works because we both respect the boundary of never discussing __________________." },
        { type: "fill", pillar: "Communication", text: "They suggested we communicate solely through __________________ to keep things strictly casual." },
        { type: "fill", pillar: "Compromise", text: "The biggest compromise in our FWB situation was agreeing to __________________." },
        { type: "thisSoundsLike", pillar: "Respect", text: "This sounds like... someone who shows respect for their FWB's space by only 'dropping by' unannounced at 3 AM." },
        { type: "fill", pillar: "Boundaries", text: "The moment our FWB boundary was crossed was when they started leaving __________________ at my place." },
        { type: "fill", pillar: "Communication", text: "Our FWB 'talks' often involved more __________________ than actual words." },
        { type: "fill", pillar: "Compromise", text: "We finally had to compromise on whether our FWB status included __________________." },
        { type: "thisSoundsLike", pillar: "Communication", text: "This sounds like... an FWB who communicates exclusively through cryptic TikTok dances." },
    ],
    "Long-Term Relationships & Marriage": [
        { type: "fill", pillar: "Communication", text: "After 10 years of marriage, we've mastered the art of communicating solely through __________________." },
        { type: "fill", pillar: "Respect", text: "Our secret to a happy marriage is respecting each other's need for __________________." },
        { type: "fill", pillar: "Compromise", text: "We knew our compromise skills were peak when we finally agreed on __________________ for dinner." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... a couple whose idea of healthy boundaries involves separate houses in different states." },
        { type: "fill", pillar: "Communication", text: "The key to our long-term happiness is never discussing __________________." },
        { type: "fill", pillar: "Respect", text: "My partner truly respects my need for solitude by always __________________." },
        { type: "fill", pillar: "Compromise", text: "Our biggest compromise yet involved deciding on __________________ for our anniversary." },
        { type: "thisSoundsLike", pillar: "Compromise", text: "This sounds like... a couple who compromises on vacation destinations by alternating between a swamp tour and a taxidermy convention." },
    ],
    "Breakups & Moving On": [
        { type: "fill", pillar: "Boundaries", text: "After the breakup, I learned to respect my own boundaries by refusing to __________________." },
        { type: "fill", pillar: "Compromise", text: "My ex tried to compromise on splitting our possessions by offering me __________________." },
        { type: "fill", pillar: "Communication", text: "The most effective post-breakup communication we had involved __________________." },
        { type: "thisSoundsLike", pillar: "Respect", text: "This sounds like... someone who, to show respect for their ex's new relationship, sends them a framed photo of their ex's ex every holiday." },
        { type: "fill", pillar: "Boundaries", text: "Setting a boundary about __________________ was crucial for my healing after the breakup." },
        { type: "fill", pillar: "Compromise", text: "There was no compromise possible when it came to who kept __________________." },
        { type: "fill", pillar: "Communication", text: "Our post-breakup communication strategy was strictly limited to __________________." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... someone who 'moves on' by getting a tattoo of their ex's face, but with devil horns." },
    ],
    "Looks Like": [
        { type: "image_judgment", pillar: "Communication", text: "What are these two *really* thinking?", ai_prompt_description: "Realistic photograph of a long-term romantic couple showing emotional distance. They are sitting on a couch in a dimly lit living room, both illuminated only by the glow of their smartphones, ignoring each other completely. The image should have a cinematic mood, with a soft focus on the space between them, highlighting their isolation from each other. The composition should feel intimate yet cold." },
        { type: "image_judgment", pillar: "Compromise", text: "Caption this scene of domestic bliss.", ai_prompt_description: "A photorealistic image capturing a failure of compromise in a shared living space. The scene is a kitchen perfectly divided in half with a sharp, jarring line. One side is sparkling clean, organized, and minimalist. The other side is a chaotic explosion of dirty dishes, spilled food, and clutter. Use vibrant colors to emphasize the stark contrast. Wide shot composition to capture the entire surreal scene of domestic conflict." },
        { type: "image_judgment", pillar: "Respect", text: "What's the story here?", ai_prompt_description: "Cinematic, realistic photograph showing a moment of disrespect between two friends in a coffee shop. One person is in the foreground, slightly out of focus, excitedly showing something on their smartphone. The focus is on the other friend's face, who is pointedly looking away with a clear expression of boredom and disinterest. Use a shallow depth of field and natural lighting to highlight the uncomfortable social dynamic." },
        { type: "image_judgment", pillar: "Boundaries", text: "Describe this situation in one word.", ai_prompt_description: "A realistic photograph capturing the violation of personal boundaries. A person is sitting at a home office desk, their face showing stress and discomfort. Their partner is looming directly over their shoulder, peering intensely at the computer screen. Use a slightly wide-angle 'over the shoulder' composition to create a crowded, claustrophobic feeling. The lighting should be harsh, casting a shadow from the standing partner over the person working, emphasizing the invasion of their space." }
    ],
    "Red Flag or Relationship Goal": [
        {
            type: "scenario_vote",
            text: "You've just introduced your partner to your closest friends, and the conversation flows, everyone seems to be getting along. Later, your friends text you saying they had a great time, but your partner immediately starts grilling you about what your friends really think, nitpicking every comment. What's going on here?",
            choices: {
                a: "Oh, they're just, you know, collecting data. Like a relationship spy trying to figure out if your squad is worthy of their presence. It's not insecurity, it's... strategic vetting. Yeah, that's it.",
                b: "Nah, that's just the prelude to the 'Great Friend Purge.' Soon, it'll be just you and them. Alone. In a quiet room. With their thoughts. Good luck with that.",
                c: "See, this is that real growth right here! They're invested, man. They wanna know if the vibes were authentic, if there's any room for improvement, because they're trying to build a strong, integrated social unit. That's a team player!",
                d: "They're trying to figure out which of your friends they can hang with when you're busy. You know, diversifying their social portfolio. Smart move. They ain't trying to just sit around waiting on you. That's freedom right there!"
            }
        }
    ]
};

const ANSWER_CARDS = [
    "a pet rock named 'Gary'", "my extensive collection of toenail clippings", "the concept of personal hygiene",
    "a five-hour interpretive dance", "my imaginary friend, Bartholomew", "the meaning of life, according to a squirrel",
    "their ex's entire family tree", "a spontaneous trip to the moon", "a heated debate about flat earth theories",
    "my obsession with competitive eating", "the ghost of my past relationship", "a really bad karaoke performance",
    "a detailed PowerPoint presentation on my insecurities", "my unique collection of lint", "a crying baby simulator",
    "the sound of a kazoo orchestra", "their unedited diary entries from middle school", "a sock puppet show",
    "the recipe for my grandma's infamous fruitcake", "a detailed analysis of my dream journal", "their pet goldfish",
    "a surprise visit from a mariachi band", "a 30-page manifesto on why cats are superior to dogs", "my crippling fear of ducks",
    "a life-sized cardboard cutout of myself", "the exact moment I realized I was allergic to gluten", "my collection of antique staplers",
    "a detailed explanation of quantum physics", "their personal alien abduction story", "a single, lonely potato",
    "the dark secrets of my childhood treehouse", "my profound love for glitter", "a dramatic re-enactment of my last dentist visit",
    "a squirrel dressed as a tiny chef", "my secret talent for burping the alphabet backwards", "a miniature alpaca farm",
    "the lingering smell of old gym socks", "my deep philosophical thoughts on cheese", "a very aggressive snail",
    "a conspiracy theory involving garden gnomes", "my questionable fashion choices from the 90s", "the universal language of farts",
    "a spontaneous opera performance", "my collection of rare, vintage sporks", "the exact number of hairs on my head",
    "a detailed map of my internal organs", "my uncanny ability to predict the weather with my knee", "a flock of angry pigeons",
    "my personal brand of awkward silence", "a very confused mime", "the entire script of a terrible B-movie",
    "a deep dive into celebrity gossip from 2003", "my most embarrassing childhood story, told in interpretive dance", "a rogue tumbleweed",
    "my collection of forgotten dreams", "a perpetually confused stare", "the subtle art of passive aggression",
    "a commitment to never folding laundry again", "the true identity of Santa Claus", "a never-ending loop of elevator music",
    "my inability to tell left from right", "a lifetime supply of questionable advice", "the secret to perfect toast",
    "a spontaneous act of kindness towards a pigeon", "my irrational fear of balloons", "a detailed budget of my impulse purchases",
    "the surprising history of garden hoses", "my unwavering dedication to napping", "a profound appreciation for mundane objects",
    "my personal record for most consecutive sneezes", "a hidden talent for imitating barnyard animals", "the shocking truth about socks",
    "my unwavering belief in unicorns", "a detailed analysis of my coffee preferences", "the silent language of eyebrow raises",
    "a deep philosophical debate about condiments", "my surprising expertise in competitive napping", "the existential dread of a Monday morning",
    "a profound understanding of cat psychology", "my secret stash of emergency chocolate", "the art of graceful awkwardness",
    "a detailed plan for world domination (starting with my pantry)", "my most cherished collection of bottle caps", "the subtle nuances of air guitar",
    "a profound appreciation for bad puns", "my impressive ability to procrastinate creatively", "the surprising comfort of mismatched socks",
    "a detailed study of dust bunnies", "my personal philosophy on the optimal way to butter toast", "the quiet rebellion of untamed hair",
    "a deep contemplation of the universe while staring at a ceiling fan", "my unwavering commitment to collecting novelty erasers", "the dramatic flair of a well-timed eye-roll"
];

// Component for displaying temporary messages/modals instead of alerts
const MessageModal = ({ message, onClose }) => {
    if (!message) return null;
    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-lg p-6 shadow-xl max-w-sm w-full text-center">
                <p className="text-xl font-semibold mb-4 text-gray-800">{message}</p>
                <button
                    onClick={onClose}
                    className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-6 rounded-full transition duration-300 transform hover:scale-105"
                >
                    OK
                </button>
            </div>
        </div>
    );
};

// Main App Component
function App() {
    const [userId, setUserId] = useState(null);
    const [currentPage, setCurrentPage] = useState('home'); // home, lobby, game, leaderboard, winner
    const [gameId, setGameId] = useState(null);
    const [gameData, setGameData] = useState(null);
    const [message, setMessage] = useState(null);
    const [isAuthReady, setIsAuthReady] = useState(false);
    const [isLoadingImage, setIsLoadingImage] = useState(false); // For host loading state
    const [playerName, setPlayerName] = useState(''); // New state for player nickname

    // Auth state listener and initial sign-in logic
    useEffect(() => {
        const SESSION_USER_ID_KEY = 'redFlagsGameTempUserId';

        const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
            const initialAuthToken = process.env.REACT_APP_INITIAL_AUTH_TOKEN;
            if (initialAuthToken) {
                // If an initial auth token is provided (e.g., from the Canvas environment)
                // Use Firebase authentication with the provided token.
                if (!currentUser) {
                    try {
                        await signInWithCustomToken(auth, initialAuthToken);
                    } catch (error) {
                        console.error("Firebase Auth Error with custom token:", error);
                        setMessage(`Authentication failed: ${error.message}. Please refresh.`);
                    }
                }
                // Once authenticated, set the user and their UID from Firebase.
                if (auth.currentUser) {
                    setUserId(auth.currentUser.uid);
                    // Set default player name if not already set, or load from storage
                    if (!playerName) {
                        setPlayerName(`Player ${auth.currentUser.uid.substring(0, 4)}`);
                    }
                }
            } else {
                // If no initial auth token (e.g., in a standalone browser tab for testing)
                // Generate and use a temporary, session-based user ID.
                let tempUserId = sessionStorage.getItem(SESSION_USER_ID_KEY);
                if (!tempUserId) {
                    tempUserId = `guest_${crypto.randomUUID()}`; // Generate a unique ID
                    sessionStorage.setItem(SESSION_USER_ID_KEY, tempUserId);
                    console.log("Generated new session userId:", tempUserId);
                } else {
                    console.log("Reusing session userId:", tempUserId);
                }

                setUserId(tempUserId); // Use the session ID as the userId
                // Set default player name for guest
                if (!playerName) {
                    setPlayerName(`Player ${tempUserId.substring(0, 5)}`);
                }
            }
            setIsAuthReady(true); // Auth listener has completed its initial check or temp ID is set
        });
        return () => unsubscribe();
    }, [playerName]); // Added playerName to dependencies to prevent re-rendering issues


    // Firebase game data listener
    useEffect(() => {
        if (!gameId || !isAuthReady || !userId) return; // Ensure auth and user are ready

        const gameDocRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
        const unsubscribeGame = onSnapshot(gameDocRef, async (docSnap) => {
            if (docSnap.exists()) {
                const data = docSnap.data();
                const oldGameData = gameData;
                setGameData(data);

                // If a new image judgment prompt was just set by the host, generate the image
                if (
                    userId === data.hostId &&
                    data.status === 'submission' &&
                    data.currentPrompt?.type === 'image_judgment' &&
                    !data.currentPrompt.imageUrl && // Only generate if no URL exists
                    (oldGameData?.currentRound !== data.currentRound || oldGameData?.status !== 'submission') // Trigger only on round change
                ) {
                    setIsLoadingImage(true);
                    const imageUrl = await generateImageForPrompt(data.currentPrompt.ai_prompt_description);
                    await updateDoc(gameDocRef, {
                        'currentPrompt.imageUrl': imageUrl,
                        isLoadingImage: false
                    });
                    setIsLoadingImage(false);
                }

                // Navigate based on game status
                if (data.status === 'playing' || data.status === 'submission' || data.status === 'voting') {
                    setCurrentPage('game');
                } else if (data.status === 'round_end') {
                    setCurrentPage('leaderboard');
                } else if (data.status === 'game_over') {
                    setCurrentPage('winner');
                } else if (data.status === 'lobby') {
                    setCurrentPage('lobby');
                }
            } else {
                console.log("Game does not exist or has been deleted.");
                setGameId(null);
                setGameData(null);
                setCurrentPage('home');
                setMessage("Game not found or ended.");
            }
        }, (error) => {
            console.error("Error listening to game data:", error);
            setMessage("Failed to load game data. Please try again.");
        });

        return () => unsubscribeGame();
    }, [gameId, isAuthReady, userId, gameData]);

    // This is a placeholder for your real image generation call.
    // Replace this with your call to the imagen-3.0-generate-002 model.
    const generateImageForPrompt = async (promptDescription) => {
        console.log("Starting image generation for:", promptDescription);
        // Simulate API call delay
        await new Promise(resolve => setTimeout(resolve, 3000)); 
        
        // In your real implementation, you would:
        // 1. Call your backend endpoint that generates the image.
        // 2. That endpoint would call the image generation model.
        // 3. It would return a base64 string or a URL to the image.
        
        // For now, we return a placeholder image URL.
        const placeholderImageUrl = `https://picsum.photos/seed/${generateId()}/800/600`;
        console.log("Finished image generation, returning placeholder:", placeholderImageUrl);
        return placeholderImageUrl;
    };

    const showMessage = (msg) => {
        setMessage(msg);
        setTimeout(() => setMessage(null), 5000); // Clear message after 5 seconds
    };

    const handleNextRound = async () => {
        if (!gameData || !userId || gameData.hostId !== userId) return;

        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            const nextRound = gameData.currentRound + 1;

            if (nextRound > gameData.maxRounds) {
                await updateDoc(gameRef, { status: 'game_over' });
            } else {
                const promptCardsForCategory = PROMPT_CARDS[gameData.category];
                const newPrompt = getRandomPrompt(promptCardsForCategory, gameData.promptHistory || []);
                const updatedPromptHistory = arrayUnion(newPrompt);

                // Reset player states for the new round, but do not replenish hands.
                // The player's hand is already updated when they submit a card.
                const updatedPlayers = {};
                for (const pId in gameData.players) {
                    updatedPlayers[pId] = {
                        ...gameData.players[pId],
                        hasSubmitted: false,
                        hasVoted: false
                    };
                }

                await updateDoc(gameRef, {
                    status: 'submission',
                    currentRound: nextRound,
                    currentPrompt: newPrompt,
                    promptHistory: updatedPromptHistory,
                    players: updatedPlayers,
                    submittedAnswers: {},
                    votes: {},
                    currentWinningSubmission: null,
                    isLoadingImage: newPrompt.type === 'image_judgment' // Set loading flag
                });

                // Image generation is now handled by the onSnapshot listener
            }
        } catch (error) {
            console.error("Error advancing round:", error);
            showMessage("Failed to advance round. Please try again.");
        }
    };

    // Render based on current page
    let content;
    switch (currentPage) {
        case 'home':
            content = <HomePage setCurrentPage={setCurrentPage} setGameId={setGameId} userId={userId} showMessage={showMessage} playerName={playerName} setPlayerName={setPlayerName} />;
            break;
        case 'lobby':
            content = <LobbyPage gameData={gameData} gameId={gameId} userId={userId} showMessage={showMessage} />;
            break;
        case 'game':
            content = <GamePage gameData={gameData} gameId={gameId} userId={userId} showMessage={showMessage} />;
            break;
        case 'leaderboard':
            content = <LeaderboardPage gameData={gameData} userId={userId} onNextRound={handleNextRound} />;
            break;
        case 'winner':
            content = <WinnerPage gameData={gameData} userId={userId} onPlayAgain={() => {
                setGameId(null);
                setGameData(null);
                setCurrentPage('home');
            }} setMessage={setMessage} />;
            break;
        default:
            content = <HomePage setCurrentPage={setCurrentPage} setGameId={setGameId} userId={userId} showMessage={showMessage} playerName={playerName} setPlayerName={setPlayerName} />;
    }

    if (!isAuthReady) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-800 via-pink-600 to-red-500 text-white font-inter">
                <p className="text-3xl animate-pulse">Loading game...</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-800 via-pink-600 to-red-500 text-white font-inter flex flex-col items-center justify-center p-4">
            <MessageModal message={message} onClose={() => setMessage(null)} />
            <h1 className="text-5xl md:text-6xl font-extrabold text-center mb-8 drop-shadow-lg leading-tight">
                Red Flags & Relationship Goals
            </h1>
            <p className="text-lg md:text-xl text-center mb-6 text-white text-opacity-90">
                Your Player ID: <span className="font-mono bg-white bg-opacity-20 rounded-md px-3 py-1 break-all">{userId}</span>
            </p>
            <div className="w-full max-w-4xl bg-white bg-opacity-10 backdrop-filter backdrop-blur-sm rounded-3xl shadow-2xl p-6 md:p-10 border border-white border-opacity-30">
                {content}
            </div>
        </div>
    );
}

// HomePage Component
const HomePage = ({ setCurrentPage, setGameId, userId, showMessage, playerName, setPlayerName }) => {
    const [joinCode, setJoinCode] = useState('');

    const handleCreateGame = async () => {
        if (!playerName.trim()) {
            showMessage("Please enter your nickname.");
            return;
        }
        try {
            const categories = Object.keys(PROMPT_CARDS);
            const randomCategory = categories[Math.floor(Math.random() * categories.length)]; // Select a random category
            const gameCode = Math.random().toString(36).substring(2, 8).toUpperCase(); // 6-char code
            console.log("Generated Game Code:", gameCode); // Debugging log

            const initialPlayers = {};
            // Use the userId passed from App component (which is now guaranteed unique per tab)
            initialPlayers[userId] = {
                id: userId, // Added unique ID for the player object
                name: playerName.trim(), // Use custom player name
                score: 0,
                isHost: true,
                hand: shuffleArray([...ANSWER_CARDS]).slice(0, 10), // Give 10 unique cards
                hasSubmitted: false,
                hasVoted: false,
                totalRedFlags: 0,
                totalHearts: 0
            };

            const newGameRef = await addDoc(collection(db, `artifacts/${appId}/public/data/games`), {
                hostId: userId,
                category: randomCategory,
                currentRound: 1,
                maxRounds: 10,
                currentPrompt: null,
                status: 'lobby', // lobby, submission, voting, round_end, game_over
                players: initialPlayers,
                submittedAnswers: {},
                playerChoices: {}, // For the new game mode
                votes: {},
                gameCode: gameCode, // Ensure gameCode is always a string
                createdAt: serverTimestamp(),
                promptHistory: [], // To store played prompts
                answerCardPool: shuffleArray([...ANSWER_CARDS]), // All available answer cards
            });
            console.log(`Game created with Firestore path: artifacts/${appId}/public/data/games/${newGameRef.id}`); // New debugging log with full path
            setGameId(newGameRef.id);
            setCurrentPage('lobby');
            showMessage(`Game created! Share code: ${gameCode}`);
        } catch (error) {
            console.error("Error creating game:", error);
            showMessage("Failed to create game. Please try again.");
        }
    };

    const handleJoinGame = async () => {
        if (!playerName.trim()) {
            showMessage("Please enter your nickname.");
            return;
        }
        if (!joinCode) {
            showMessage("Please enter a game code.");
            return;
        }

        try {
            console.log(`Attempting to query Firestore for collection: artifacts/${appId}/public/data/games with gameCode: ${joinCode.toUpperCase()}`); // More detailed debugging log
            const q = query(collection(db, `artifacts/${appId}/public/data/games`), where("gameCode", "==", joinCode.toUpperCase()));
            const querySnapshot = await getDocs(q);
            console.log("Query Snapshot empty:", querySnapshot.empty); // Debugging log

            if (!querySnapshot.empty) {
                const gameDoc = querySnapshot.docs[0];
                const gameData = gameDoc.data();
                const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameDoc.id);

                if (gameData.players[userId]) {
                    showMessage("You are already in this game.");
                    setGameId(gameDoc.id);
                    setCurrentPage('lobby'); // Go to lobby if already in
                    return;
                }

                // If joining after game started, give initial cards
                const playerHand = shuffleArray([...ANSWER_CARDS]).slice(0, 10);

                await updateDoc(gameRef, {
                    [`players.${userId}`]: {
                        id: userId, // Added unique ID for the player object
                        name: playerName.trim(), // Use custom player name
                        score: 0,
                        isHost: false,
                        hand: playerHand,
                        hasSubmitted: false,
                        hasVoted: false,
                        totalRedFlags: 0,
                        totalHearts: 0
                    }
                });
                setGameId(gameDoc.id);
                setCurrentPage('lobby'); // Will change based on game status from listener
                showMessage(`Joined game ${joinCode}!`);
            } else {
                showMessage("Invalid game code.");
            }
        } catch (error) {
            console.error("Error joining game:", error);
            showMessage("Failed to join game. Please try again.");
        }
    };

    return (
        <div className="text-center">
            <input
                type="text"
                placeholder="Enter your nickname"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                className="w-full p-3 rounded-xl bg-white bg-opacity-20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 mb-4 text-center"
                maxLength="20" // Limit nickname length
            />
            <button
                onClick={handleCreateGame}
                className="w-full mb-4 bg-gradient-to-r from-blue-500 to-teal-400 hover:from-blue-600 hover:to-teal-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
            >
                Create New Game
            </button>
            <div className="mb-6">
                <p className="text-gray-200 mb-2">OR</p>
                <input
                    type="text"
                    placeholder="Enter Game Code"
                    value={joinCode}
                    onChange={(e) => setJoinCode(e.target.value)}
                    className="w-full p-3 rounded-xl bg-white bg-opacity-20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 mb-3 text-center uppercase"
                    maxLength="6"
                />
                <button
                    onClick={handleJoinGame}
                    className="w-full bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                >
                    Join Game
                </button>
            </div>
            <p className="text-sm text-gray-300 mt-4">
                Minimum 2 players needed to start a game.
            </p>
        </div>
    );
};

// LobbyPage Component
const LobbyPage = ({ gameData, gameId, userId, showMessage }) => {
    if (!gameData) return <p className="text-center text-xl text-gray-200">Loading lobby...</p>;

    const players = Object.values(gameData.players || {}).sort((a, b) => a.name.localeCompare(b.name));
    const isHost = gameData.hostId === userId;
    const canStartGame = players.length >= 2;
    const gameCode = gameData.gameCode;

    const handleCategoryChange = async (e) => {
        if (!isHost) return;
        const newCategory = e.target.value;
        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            await updateDoc(gameRef, { category: newCategory });
        } catch (error) {
            console.error("Error updating category:", error);
            showMessage("Failed to update category.");
        }
    };

    const handleStartGame = async () => {
        if (!isHost) return;
        if (!canStartGame) {
            showMessage("Need at least 2 players to start the game.");
            return;
        }

        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            const promptCardsForCategory = PROMPT_CARDS[gameData.category];

            // Initialize played prompts to avoid duplicates
            const initialPromptHistory = [];

            // Reset player states for new game
            const updatedPlayers = {};
            for (const pId in gameData.players) {
                // Replenish player hands for the new round
                const currentHand = gameData.players[pId].hand || [];
                const cardsNeeded = 10 - currentHand.length;
                const newCards = gameData.answerCardPool.slice(0, cardsNeeded);
                const remainingPool = gameData.answerCardPool.slice(cardsNeeded);

                updatedPlayers[pId] = {
                    ...gameData.players[pId],
                    score: 0, // Reset scores for new game
                    hand: [...currentHand, ...newCards], // Replenish hand
                    hasSubmitted: false,
                    hasVoted: false
                };
                gameData.answerCardPool = remainingPool; // Update pool immediately for next player in loop
            }

            // Draw initial prompt
            const initialPrompt = getRandomPrompt(promptCardsForCategory, initialPromptHistory);
            initialPromptHistory.push(initialPrompt); // Add to history

            await updateDoc(gameRef, {
                status: 'submission', // Start the game in submission phase
                currentRound: 1,
                currentPrompt: initialPrompt,
                promptHistory: initialPromptHistory,
                players: updatedPlayers,
                submittedAnswers: {},
                votes: {},
                answerCardPool: gameData.answerCardPool, // Save updated pool to Firestore
                isLoadingImage: initialPrompt.type === 'image_judgment' // Set loading flag
            });

             // Image generation is now handled by the onSnapshot listener in the App component,
             // which is safer and avoids trying to manage state across components.
        } catch (error) {
            console.error("Error starting game:", error);
            showMessage("Failed to start game. Please try again.");
        }
    };

    return (
        <div className="text-center p-4">
            <h2 className="text-4xl font-bold mb-4 text-white">Game Lobby</h2>
            <p className="text-xl text-gray-200 mb-6">
                Game Code: <span className="font-bold text-yellow-300">{gameCode}</span>
            </p>
            <div className="text-lg text-gray-200 mb-2 flex items-center justify-center">
                <span className="mr-2">Category:</span>
                {isHost ? (
                    <select
                        value={gameData.category}
                        onChange={handleCategoryChange}
                        className="p-2 rounded-lg bg-white bg-opacity-20 text-white font-semibold focus:outline-none focus:ring-2 focus:ring-blue-400"
                    >
                        {Object.keys(PROMPT_CARDS).map(cat => (
                            <option key={cat} value={cat} className="text-black">{cat}</option>
                        ))}
                    </select>
                ) : (
                    <span className="font-semibold text-pink-200">{gameData.category}</span>
                )}
            </div>
            <p className="text-lg text-gray-200 mb-4">Players Connected:</p>
            <ul className="list-disc list-inside text-left mx-auto max-w-xs mb-8 text-gray-100">
                {players.map(player => (
                    <li key={player.id} className="py-1">
                        {player.name} {player.isHost && "(Host)"}
                    </li>
                ))}
            </ul>
            {isHost && (
                <button
                    onClick={handleStartGame}
                    disabled={!canStartGame}
                    className={`w-full py-4 px-8 rounded-full shadow-lg transition duration-300 transform ${
                        canStartGame
                            ? 'bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold hover:scale-105'
                            : 'bg-gray-600 text-gray-300 cursor-not-allowed'
                    }`}
                >
                    {canStartGame ? 'Start Game' : `Need ${2 - players.length} more player(s) to start`}
                </button>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Waiting for host to start the game!</p>
            )}
        </div>
    );
};

// GamePage Component
const GamePage = ({ gameData, gameId, userId, showMessage }) => {
    if (!gameData || !gameData.currentPrompt) {
        return <p className="text-center text-xl text-gray-200">Waiting for game to start...</p>;
    }

    return <GameRound gameData={gameData} gameId={gameId} userId={userId} showMessage={showMessage} />;
};

const GameRound = ({ gameData, gameId, userId, showMessage }) => {
    // State for player's current action
    const [selectedCard, setSelectedCard] = useState(null);
    const [customAnswer, setCustomAnswer] = useState('');
    const [nominationData, setNominationData] = useState({ nominatedPlayerId: '', justification: '' });
    const [selectedVote, setSelectedVote] = useState(null);
    const [selectedChoice, setSelectedChoice] = useState(null); // For scenario_vote

    const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
    const currentPlayer = gameData.players[userId];
    const isHost = gameData.hostId === userId;
    const currentPrompt = gameData.currentPrompt;
    const submittedAnswers = Object.values(gameData.submittedAnswers || {}).sort((a, b) => (a.submittedAt?.seconds || 0) - (b.submittedAt?.seconds || 0));
    const playersInGame = Object.values(gameData.players || {}).sort((a, b) => a.name.localeCompare(b.name));

    // Reset local state when game phase changes
    useEffect(() => {
        setSelectedCard(null);
        setCustomAnswer('');
        setNominationData({ nominatedPlayerId: '', justification: '' });
        setSelectedVote(null);
        setSelectedChoice(null);
    }, [gameData.status, gameData.currentRound]);

    const handleSelectCard = (card) => {
        setSelectedCard(card);
        setCustomAnswer(''); // Clear custom answer if a card is selected
    };

    const handleCustomAnswerChange = (e) => {
        setCustomAnswer(e.target.value);
        setSelectedCard("BLANK_CARD_SELECTED"); // Mark as blank card chosen
    };

    const handleNominationChange = (field, value) => {
        setNominationData(prev => ({ ...prev, [field]: value }));
    };

    const handleSubmitAnswer = async () => {
        if (currentPlayer.hasSubmitted) {
            showMessage("You have already submitted an answer for this round.");
            return;
        }

        let answerContent = '';
        if (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') {
            if (selectedCard === null) {
                showMessage("Please select a card or type a custom answer.");
                return;
            }
            if (selectedCard === "BLANK_CARD_SELECTED") {
                if (!customAnswer.trim()) {
                    showMessage("Please type your custom answer.");
                    return;
                }
                answerContent = customAnswer.trim();
            } else {
                answerContent = selectedCard;
            }
        } else if (currentPrompt.type === 'thisSoundsLike') {
            if (!nominationData.nominatedPlayerId || !nominationData.justification.trim()) {
                showMessage("Please select a player and provide a justification.");
                return;
            }
            answerContent = {
                nominatedPlayerId: nominationData.nominatedPlayerId,
                justification: nominationData.justification.trim()
            };
        } else {
            showMessage("Invalid prompt type.");
            return;
        }

        try {
            const submissionId = generateId();
            const updates = {
                [`players.${userId}.hasSubmitted`]: true,
                // Only remove the card if it was selected from the hand
                [`players.${userId}.hand`]: (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && selectedCard !== "BLANK_CARD_SELECTED"
                    ? currentPlayer.hand.filter(card => card !== selectedCard)
                    : currentPlayer.hand,
                [`submittedAnswers.${submissionId}`]: {
                    id: submissionId,
                    playerId: userId,
                    answer: answerContent,
                    votes: 0,
                    voters: [],
                    submittedAt: serverTimestamp() // Add timestamp for stable sorting
                }
            };

            await updateDoc(gameRef, updates);
            showMessage("Answer submitted!");
        } catch (error) {
            console.error("Error submitting answer:", error);
            showMessage("Failed to submit answer. Please try again.");
        }
    };

    const handleSubmitChoice = async () => {
        if (!selectedChoice) {
            showMessage("Please select a choice.");
            return;
        }
        try {
            const updates = {
                [`players.${userId}.hasSubmitted`]: true,
                [`playerChoices.${userId}`]: selectedChoice
            };
            await updateDoc(gameRef, updates);
            showMessage("Your choice has been submitted!");
        } catch (error) {
            console.error("Error submitting choice:", error);
            showMessage("Failed to submit choice. Please try again.");
        }
    };

    // Check if all active players have submitted their answers
    const submittedCount = currentPrompt.type === 'scenario_vote'
        ? Object.keys(gameData.playerChoices || {}).length
        : submittedAnswers.length;


    // Host manages phase transitions
    useEffect(() => {
        // Only trigger if current phase is 'submission' and all relevant players have submitted
        if (isHost && gameData.status === 'submission' && submittedCount === playersInGame.length && playersInGame.length > 0) {
            updateDoc(gameRef, { status: 'voting' }).catch(e => console.error("Error setting status to voting:", e));
        }
    }, [submittedCount, playersInGame, isHost, gameData.status, gameRef]);


    const handleScenarioVote = async (targetPlayerId, voteType) => {
        if (currentPlayer.hasVoted) {
            showMessage("You have already submitted your votes for this round.");
            return;
        }

        const votePath = `votes.${targetPlayerId}.${voteType}`;
        try {
            await updateDoc(gameRef, {
                [votePath]: arrayUnion(userId)
            });
            showMessage(`${voteType === 'hearts' ? 'Heart' : 'Red Flag'} given!`);
        } catch (error) {
            console.error("Error casting scenario vote:", error);
            showMessage("Failed to cast vote.");
        }
    };

    const handleLockInVotes = async () => {
        if (currentPlayer.hasVoted) return;
        try {
            await updateDoc(gameRef, { [`players.${userId}.hasVoted`]: true });
            showMessage("Votes locked in!");
        } catch (error) {
            console.error("Error locking in votes:", error);
            showMessage("Failed to lock in votes.");
        }
    };

    const handleVote = async (submissionId) => {
        if (currentPlayer.hasVoted) {
            showMessage("You have already voted this round.");
            return;
        }

        const submission = gameData.submittedAnswers[submissionId];
        if (!submission) return;

        // Prevent voting for own card for fill-in-the-blank or image judgment
        if ((currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && submission.playerId === userId) {
            showMessage("You cannot vote for your own answer.");
            return;
        }

        // Prevent voting for own nomination or if you are the nominee for "this sounds like"
        if (currentPrompt.type === 'thisSoundsLike') {
            if (submission.playerId === userId) {
                showMessage("You cannot vote for your own nomination.");
                return;
            }
            if (submission.answer.nominatedPlayerId === userId) {
                showMessage("You cannot vote on a nomination where you were the nominee.");
                return;
            }
        }

        try {
            await updateDoc(gameRef, {
                [`submittedAnswers.${submissionId}.votes`]: (submission.votes || 0) + 1,
                [`submittedAnswers.${submissionId}.voters`]: arrayUnion(userId),
                [`players.${userId}.hasVoted`]: true
            });
            setSelectedVote(submissionId); // Mark local state
            showMessage("Vote cast!");
        } catch (error) {
            console.error("Error casting vote:", error);
            showMessage("Failed to cast vote. Please try again.");
        }
    };

    // Check if all players have voted
    const votersCount = playersInGame.filter(p => p.hasVoted).length;
    const allPlayersVoted = votersCount === playersInGame.length;


    useEffect(() => {
        // Trigger only if current phase is 'voting' and all players have voted
        if (isHost && gameData.status === 'voting' && allPlayersVoted && votersCount > 0) {
            // All players voted, move to round end phase
            // Calculate winner and update scores
            let winningSubmission = null;
            let maxVotes = -1;

            for (const subId in gameData.submittedAnswers) {
                const sub = gameData.submittedAnswers[subId];
                if (sub.votes > maxVotes) {
                    maxVotes = sub.votes;
                    winningSubmission = sub;
                } else if (sub.votes === maxVotes) {
                    // Tie-breaker: pick one randomly (or based on submission time if available)
                    // A more stable tie-breaker would be to use the submission timestamp
                    if ((winningSubmission.submittedAt?.seconds || Infinity) > (sub.submittedAt?.seconds || 0)) {
                        winningSubmission = sub;
                    }
                }
            }

            const updates = {
                status: 'round_end',
                currentWinningSubmission: winningSubmission ? {
                    id: winningSubmission.id,
                    playerId: winningSubmission.playerId,
                    answer: winningSubmission.answer,
                    votes: winningSubmission.votes || 0 // Save vote count
                } : null,
            };

            if (winningSubmission) {
                const winningPlayerId = winningSubmission.playerId;
                updates[`players.${winningPlayerId}.score`] = (gameData.players[winningPlayerId].score || 0) + 1;
            }

            updateDoc(gameRef, updates).catch(e => console.error("Error setting status to round_end:", e));
        }
    }, [allPlayersVoted, isHost, gameData.status, votersCount, gameData.submittedAnswers, gameData.players, gameRef]); // Added votersCount to dependencies for accuracy


    const playerHand = currentPlayer?.hand || [];
    const hasSubmittedThisRound = currentPlayer?.hasSubmitted;
    const hasVotedThisRound = currentPlayer?.hasVoted;

    if (gameData.isLoadingImage) {
        return (
            <div className="text-center p-4">
                <h2 className="text-3xl font-bold mb-4 text-white animate-pulse">Generating AI Image...</h2>
                <p className="text-xl text-gray-200">The host is creating a new visual prompt. Please wait.</p>
            </div>
        );
    }

    return (
        <div className="flex flex-col items-center justify-center space-y-6">
            <div className="text-center bg-white bg-opacity-20 rounded-xl p-4 md:p-6 shadow-xl w-full max-w-2xl border border-white border-opacity-30">
                <p className="text-xl font-bold text-yellow-200 mb-2">{currentPrompt.pillar}</p>
                <p className="text-3xl md:text-4xl font-extrabold text-white leading-tight"
                    dangerouslySetInnerHTML={{ __html: currentPrompt.text.replace(/__________________/g, '<span class="text-pink-300 underline">BLANK</span>') }}
                >
                </p>
                {currentPrompt.type === 'image_judgment' && currentPrompt.imageUrl && (
                    <img src={currentPrompt.imageUrl} alt={currentPrompt.ai_prompt_description} className="mt-4 rounded-lg shadow-lg mx-auto" />
                )}
                <p className="mt-4 text-lg text-gray-200">
                    Round {gameData.currentRound} of {gameData.maxRounds}
                </p>
            </div>

            {gameData.status === 'submission' && (
                <div className="w-full text-center">
                    {!hasSubmittedThisRound ? (
                        <>
                            <h3 className="text-2xl font-bold text-white mb-4">Your Turn!</h3>
                            {currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment' ? (
                                <>
                                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 mb-6">
                                        {playerHand.map((card, index) => (
                                            <button
                                                key={card + index} // Use card content + index as key for uniqueness
                                                onClick={() => handleSelectCard(card)}
                                                className={`p-4 rounded-xl text-gray-900 font-semibold shadow-md transition transform ${
                                                    selectedCard === card ? 'bg-blue-300 scale-105 ring-4 ring-blue-500' : 'bg-white hover:bg-gray-100 hover:scale-105'
                                                }`}
                                            >
                                                {card}
                                            </button>
                                        ))}
                                        {/* Blank card for custom answer */}
                                        <button
                                            onClick={() => handleSelectCard("BLANK_CARD_SELECTED")}
                                            className={`p-4 rounded-xl text-gray-900 font-semibold shadow-md transition transform ${
                                                selectedCard === "BLANK_CARD_SELECTED" ? 'bg-blue-300 scale-105 ring-4 ring-blue-500' : 'bg-yellow-300 hover:bg-yellow-200 hover:scale-105'
                                            }`}
                                        >
                                            Type Your Own!
                                        </button>
                                    </div>
                                    {selectedCard === "BLANK_CARD_SELECTED" && (
                                        <input
                                            type="text"
                                            placeholder="Type your answer here..."
                                            value={customAnswer}
                                            onChange={handleCustomAnswerChange}
                                            className="w-full p-3 rounded-xl bg-white text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-400 mb-4"
                                            maxLength="50" // Limit length for answers
                                        />
                                    )}
                                </>
                            ) : ( // This Sounds Like scenario
                                <div className="space-y-4 mb-6">
                                    <select
                                        value={nominationData.nominatedPlayerId}
                                        onChange={(e) => handleNominationChange('nominatedPlayerId', e.target.value)}
                                        className="w-full p-3 rounded-xl bg-white text-gray-900 focus:outline-none focus:ring-2 focus:ring-blue-400"
                                    >
                                        <option value="">Select a player to nominate</option>
                                        {playersInGame.map(p => (
                                            <option key={p.id} value={p.id}>{p.name}</option>
                                        ))}
                                    </select>
                                    <textarea
                                        placeholder="Justify your nomination in one short sentence..."
                                        value={nominationData.justification}
                                        onChange={(e) => handleNominationChange('justification', e.target.value)}
                                        className="w-full p-3 rounded-xl bg-white text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-400 h-24 resize-none"
                                        maxLength="100" // Limit justification length
                                    ></textarea>
                                </div>
                            )}
                            <button
                                onClick={handleSubmitAnswer}
                                className="w-full bg-gradient-to-r from-teal-500 to-cyan-600 hover:from-teal-600 hover:to-cyan-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                                disabled={((currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && !selectedCard) || (selectedCard === "BLANK_CARD_SELECTED" && !customAnswer.trim()) || (currentPrompt.type === 'thisSoundsLike' && (!nominationData.nominatedPlayerId || !nominationData.justification.trim()))}
                            >
                                Submit Answer
                            </button>
                        </>
                    ) : (
                        <p className="text-2xl text-gray-200 animate-pulse">Waiting for other players to submit...</p>
                    )}
                    <p className="text-gray-300 mt-4 text-lg">
                        Submitted: {submittedCount} / {playersInGame.length}
                    </p>
                </div>
            )}

            {gameData.status === 'submission' && currentPrompt.type === 'scenario_vote' && (
                <div className="w-full text-center">
                    {!currentPlayer.hasSubmitted ? (
                        <>
                            <h3 className="text-2xl font-bold text-white mb-4">What's your take?</h3>
                            <div className="space-y-3 mb-6">
                                {Object.entries(currentPrompt.choices).map(([key, value]) => (
                                    <button
                                        key={key}
                                        onClick={() => setSelectedChoice(key)}
                                        className={`w-full text-left p-4 rounded-xl shadow-md transition transform ${
                                            selectedChoice === key ? 'bg-blue-400 scale-105 ring-4 ring-blue-500 text-white' : 'bg-white hover:bg-gray-100 text-gray-900'
                                        }`}
                                    >
                                        <span className="font-bold uppercase mr-3">{key})</span>{value}
                                    </button>
                                ))}
                            </div>
                            <button
                                onClick={handleSubmitChoice}
                                disabled={!selectedChoice}
                                className="w-full bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 disabled:opacity-50"
                            >
                                Submit Choice
                            </button>
                        </>
                    ) : (
                        <p className="text-2xl text-gray-200 animate-pulse">Waiting for other players to submit their take...</p>
                    )}
                     <p className="text-gray-300 mt-4 text-lg">
                        Submitted: {submittedCount} / {playersInGame.length}
                    </p>
                </div>
            )}

            {gameData.status === 'voting' && currentPrompt.type !== 'scenario_vote' && (
                <div className="w-full text-center">
                    <h3 className="text-2xl font-bold text-white mb-4">Vote for your favorite!</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                        {submittedAnswers.length > 0 ? submittedAnswers.map(submission => {
                            const submitterName = gameData.players[submission.playerId]?.name || 'Unknown Player';
                            
                            let isVoteDisabled = false;
                            if (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') {
                                isVoteDisabled = submission.playerId === userId;
                            } else if (currentPrompt.type === 'thisSoundsLike') {
                                isVoteDisabled = submission.playerId === userId || (submission.answer && submission.answer.nominatedPlayerId === userId);
                            }

                            let cardText;
                            if (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') {
                                cardText = submission.answer;
                            } else {
                                const nominatedPlayerName = (submission.answer && gameData.players[submission.answer.nominatedPlayerId]?.name) || 'Unknown Player'; // Check submission.answer exists
                                cardText = `"${(submission.answer && submission.answer.justification) || ''}" - For ${nominatedPlayerName}`; // Check submission.answer exists
                            }

                            return (
                                <button
                                    key={submission.id}
                                    onClick={() => handleVote(submission.id)}
                                    className={`relative p-6 rounded-3xl shadow-xl transition transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-offset-2 ${
                                        selectedVote === submission.id ? 'bg-blue-400 ring-blue-500' :
                                        (isVoteDisabled) ? 'bg-gray-400 cursor-not-allowed opacity-70' :
                                        'bg-white hover:bg-gray-100'
                                    } ${hasVotedThisRound ? 'opacity-70 cursor-not-allowed' : ''}`}
                                    disabled={hasVotedThisRound || isVoteDisabled}
                                >
                                    <div className="absolute -top-4 -left-4 bg-yellow-400 text-gray-900 text-xs font-bold px-3 py-1 rounded-full shadow-md">
                                        Submitted by {submitterName}
                                    </div>
                                    <p className="text-xl font-semibold text-gray-900 mt-2">
                                        {cardText}
                                    </p>
                                    {/* Vote tally is now hidden during voting */}
                                </button>
                            );
                        }) : (
                            <p className="col-span-full text-xl text-gray-300">No answers submitted yet.</p>
                        )}
                    </div>
                    {hasVotedThisRound ? (
                        <p className="text-2xl text-gray-200 animate-pulse">Waiting for other players to vote...</p>
                    ) : (
                        <p className="text-lg text-gray-300">Choose the funniest or most fitting answer!</p>
                    )}
                    <p className="text-gray-300 mt-4 text-lg">
                        Voted: {votersCount} / {playersInGame.length}
                    </p>
                </div>
            )}

            {gameData.status === 'voting' && currentPrompt.type === 'scenario_vote' && (
                 <div className="w-full text-center">
                    <h3 className="text-2xl font-bold text-white mb-4">Judge Their Takes!</h3>
                    <p className="text-gray-300 mb-6">Give a ❤️ for a relationship goal or a 🚩 for a red flag.</p>
                    <div className="space-y-4 mb-6">
                        {playersInGame.filter(p => p.id !== userId).map(player => {
                            const choiceKey = gameData.playerChoices[player.id];
                            const choiceText = choiceKey ? currentPrompt.choices[choiceKey] : "No choice submitted.";
                            const hearts = gameData.votes?.[player.id]?.hearts || [];
                            const redFlags = gameData.votes?.[player.id]?.redFlags || [];
                            const hasVotedHeart = hearts.includes(userId);
                            const hasVotedFlag = redFlags.includes(userId);

                            return (
                                <div key={player.id} className="bg-white bg-opacity-20 rounded-xl p-4 text-left">
                                    <p className="font-bold text-white text-lg">{player.name}'s take:</p>
                                    <p className="text-gray-200 mb-3">"{choiceText}"</p>
                                    <div className="flex justify-end items-center space-x-3">
                                        <button
                                            onClick={() => handleScenarioVote(player.id, 'hearts')}
                                            disabled={hasVotedHeart || hasVotedFlag || currentPlayer.hasVoted}
                                            className={`px-4 py-2 rounded-full font-bold text-2xl transition transform hover:scale-110 disabled:opacity-50 ${hasVotedHeart ? 'bg-pink-400' : 'bg-gray-300 hover:bg-pink-300'}`}
                                        >
                                            ❤️
                                        </button>
                                        <button
                                            onClick={() => handleScenarioVote(player.id, 'redFlags')}
                                            disabled={hasVotedHeart || hasVotedFlag || currentPlayer.hasVoted}
                                            className={`px-4 py-2 rounded-full font-bold text-2xl transition transform hover:scale-110 disabled:opacity-50 ${hasVotedFlag ? 'bg-red-500' : 'bg-gray-300 hover:bg-red-400'}`}
                                        >
                                            🚩
                                        </button>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                    {!currentPlayer.hasVoted && (
                        <button onClick={handleLockInVotes} className="w-full bg-gradient-to-r from-green-500 to-lime-400 text-white font-bold py-4 px-8 rounded-full shadow-lg">
                            Lock In Your Votes
                        </button>
                    )}
                     <p className="text-gray-300 mt-4 text-lg">
                        Voted: {votersCount} / {playersInGame.length}
                    </p>
                </div>
            )}
        </div>
    );
};

// LeaderboardPage Component
const LeaderboardPage = ({ gameData, userId, onNextRound }) => {
    if (!gameData || !gameData.players) return <p className="text-center text-xl text-gray-200">Loading leaderboard...</p>;

    const isHost = gameData.hostId === userId;
    const players = Object.values(gameData.players);
    const sortedPlayers = [...players].sort((a, b) => {
        if (b.score !== a.score) {
            return b.score - a.score;
        }
        return a.name.localeCompare(b.name); // Alphabetical for tie-breaking
    });
    const winningSubmission = gameData.currentWinningSubmission;
    const currentPrompt = gameData.currentPrompt;

    let winningAnswerText = "No winning answer this round.";
    let winningPlayerName = "";

    if (winningSubmission) {
        winningPlayerName = gameData.players[winningSubmission.playerId]?.name || 'Unknown Player';
        if (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') {
            winningAnswerText = winningSubmission.answer;
        } else {
            const nominatedPlayerName = (winningSubmission.answer && gameData.players[winningSubmission.answer.nominatedPlayerId]?.name) || 'Unknown Player';
            winningAnswerText = `"${(winningSubmission.answer && winningSubmission.answer.justification) || ''}" - For ${nominatedPlayerName}`;
        }
    }

    return (
        <div className="text-center p-4">
            <h2 className="text-4xl font-bold text-white mb-6">Round {gameData.currentRound} Results</h2>

            <div className="bg-white bg-opacity-20 rounded-xl p-4 md:p-6 mb-8 shadow-xl border border-white border-opacity-30">
                <p className="text-lg font-semibold text-yellow-200 mb-2">{currentPrompt.pillar}</p>
                <p className="text-2xl md:text-3xl font-extrabold text-white mb-4 leading-tight">
                    Question: <span dangerouslySetInnerHTML={{ __html: currentPrompt.text.replace(/__________________/g, '______') }} />
                </p>
                <p className="text-2xl md:text-3xl font-bold text-pink-300 mb-2">Winning Answer:</p>
                <p className="text-3xl md:text-4xl italic text-white leading-tight">
                    "{winningAnswerText}"
                </p>
                {winningSubmission && (
                    <p className="text-xl text-gray-200 mt-2">
                        Submitted by: <span className="font-semibold text-blue-200">{winningPlayerName}</span>
                        <span className="ml-4 font-bold text-yellow-300">({winningSubmission.votes} {winningSubmission.votes === 1 ? 'Vote' : 'Votes'})</span>
                    </p>
                )}
            </div>

            <h3 className="text-3xl font-bold text-white mb-4">Leaderboard</h3>
            <ul className="space-y-3 mb-8">
                {sortedPlayers.map((player, index) => (
                    <li key={player.id} className={`flex items-center justify-between p-4 rounded-xl shadow-md ${
                        index === 0 ? 'bg-yellow-300 text-gray-900 font-bold text-xl' : 'bg-white bg-opacity-30 text-white'
                    }`}>
                        <span className="flex items-center">
                            {index === 0 && <span className="mr-2 text-2xl">👑</span>}
                            {player.name}
                        </span>
                        <span className="font-bold text-xl">{player.score} {player.score === 1 ? 'Point' : 'Points'}</span>
                    </li>
                ))}
            </ul>

            {isHost && (
                <button
                    onClick={onNextRound}
                    className="w-full bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                >
                    {gameData.currentRound < gameData.maxRounds ? 'Next Round' : 'End Game'}
                </button>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Waiting for host to continue...</p>
            )}
        </div>
    );
};

// WinnerPage Component
const WinnerPage = ({ gameData, userId, onPlayAgain, setMessage }) => {
    if (!gameData || !gameData.players) return <p className="text-center text-xl text-gray-200">Calculating winner...</p>;

    const players = Object.values(gameData.players);
    const sortedPlayers = [...players].sort((a, b) => {
        if (b.score !== a.score) {
            return b.score - a.score;
        }
        return a.name.localeCompare(b.name); // Alphabetical for tie-breaking
    });
    const winner = sortedPlayers[0];
    const isHost = gameData.hostId === userId;

    const winnerHeadline = {
        "First Dates": "First Date Fiasco Champion!",
        "Proposals & Engagements": "Master of Matrimony!",
        "Family & Children": "Family Funniest!",
        "Friends with Benefits (FWB)": "FWB Finesse King/Queen!",
        "Long-Term Relationships & Marriage": "Relationship Guru!",
        "Breakups & Moving On": "Breakup Boss!"
    };

    const handleDeleteGame = async () => {
        // Replaced window.confirm with MessageModal for consistency and better UX
        setMessage("Are you sure you want to delete this game? This action cannot be undone. Click OK to confirm.", () => {
            performDeleteGame();
        });
    };

    const performDeleteGame = async () => {
        try {
            await deleteDoc(doc(db, `artifacts/${appId}/public/data/games`, gameData.id)); // gameData.id holds the document ID
            onPlayAgain(); // Go back to home page
        } catch (error) {
            console.error("Error deleting game:", error);
            setMessage("Failed to delete game. Please try again.");
        }
    };


    return (
        <div className="text-center p-4">
            <h2 className="text-5xl font-extrabold text-yellow-300 mb-6 drop-shadow-lg">
                Game Over!
            </h2>
            <p className="text-3xl font-bold text-white mb-8">
                {winnerHeadline[gameData.category] || "Game Champion!"}
            </p>

            <div className="bg-white bg-opacity-20 rounded-xl p-6 mb-8 shadow-xl border border-white border-opacity-30">
                <p className="text-4xl md:text-5xl font-extrabold text-pink-300 mb-4">
                    👑 {winner.name} 👑
                </p>
                <p className="text-3xl text-white">
                    <span className="font-bold">{winner.score}</span> {winner.score === 1 ? 'Point' : 'Points'}
                </p>
            </div>

            <h3 className="text-2xl font-bold text-white mb-4">Final Leaderboard</h3>
            <ul className="space-y-2 mb-8">
                {sortedPlayers.map((player, index) => (
                    <li key={player.id} className={`flex items-center justify-between p-3 rounded-lg shadow-sm ${
                        index === 0 ? 'bg-yellow-200 text-gray-900 font-bold' : 'bg-white bg-opacity-20 text-white'
                    }`}>
                        <span>{player.name}</span>
                        <span>{player.score} {player.score === 1 ? 'Point' : 'Points'}</span>
                    </li>
                ))}
            </ul>

            {isHost && (
                <>
                    <button
                        onClick={onPlayAgain}
                        className="w-full bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 mb-4"
                    >
                        Play Again!
                    </button>
                    <button
                        onClick={handleDeleteGame}
                        className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                    >
                        Delete This Game
                    </button>
                </>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Ask the host to start a new game!</p>
            )}
        </div>
    );
};

// Utility function to shuffle an array (Fisher-Yates)
const shuffleArray = (array) => {
    let currentIndex = array.length, randomIndex;
    while (currentIndex !== 0) {
        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex--;
        [array[currentIndex], array[randomIndex]] = [array[randomIndex], array[currentIndex]];
    }
    return array;
};

// Utility function to get a random prompt that hasn't been played yet
const getRandomPrompt = (promptCards, playedPrompts) => {
    const availablePrompts = promptCards.filter(p =>
        !playedPrompts.some(pp => pp.text === p.text && pp.type === p.type)
    );

    if (availablePrompts.length === 0) {
        // If all prompts have been played, reset history and start over
        console.warn("All prompts played for this category. Resetting prompt history and picking from all cards.");
        const allPrompts = Object.values(PROMPT_CARDS).flat();
        return allPrompts[Math.floor(Math.random() * allPrompts.length)];
    }

    const randomIndex = Math.floor(Math.random() * availablePrompts.length);
    return availablePrompts[randomIndex];
};

export default App;
