import React, { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, getDoc, addDoc, setDoc, updateDoc, deleteDoc, onSnapshot, collection, query, where, arrayUnion, arrayRemove, serverTimestamp, getDocs, deleteField, increment } from 'firebase/firestore';

// Ensure __app_id and __firebase_config are defined in the environment
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};

// Initialize Firebase outside the component to avoid re-initialization
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// Helper for generating unique IDs (not cryptographically secure, but fine for game items)
const generateId = () => Math.random().toString(36).substring(2, 11);

// Pre-defined game data
const PROMPT_CARDS = {
    "First Dates": [
        { type: "fill", pillar: "Boundaries", text: "My date set a clear boundary by stating they wouldn't discuss anything before __________________." },
        { type: "fill", pillar: "Communication", text: "I knew there would be communication challenges when their opening line was, 'So, do you believe in __________________?'" },
        { type: "fill", pillar: "Respect", text: "My date showed a shocking lack of respect when they started __________________ mid-conversation." },
        { type: "thisSoundsLike", pillar: "Compromise", text: "This sounds like... someone who, on a first date, orders for both of you and then says, 'No compromise on the appetizers!'" },
        { type: "fill", pillar: "Boundaries", text: "The biggest red flag on our first date was when they instantly started talking about __________________." },
        { type: "fill", pillar: "Communication", text: "Their idea of 'getting to know you' involved asking about my __________________." },
        { type: "fill", pillar: "Respect", text: "I lost all respect for them when they pulled out a __________________ at the table." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... someone who brings their ex to a first date as a 'wingman'." },
        {
            type: "image_judgment",
            pillar: "Boundaries",
            text: "What's your red flag reaction to this scene?",
            ai_prompt_description: "A photo of a person setting up a large, elaborate, and overly aggressive 'no trespassing' sign with barbed wire around a small, innocent-looking flower bed."
        },
    ],
    "Proposals & Engagements": [
        { type: "fill", pillar: "Compromise", text: "When I tried to compromise on the wedding theme, they firmly stated, 'It's either __________________ or nothing!'" },
        { type: "fill", pillar: "Communication", text: "They proposed using an elaborate code only they understood, which made for terrible __________________." },
        { type: "fill", pillar: "Respect", text: "My partner showed their true respect for my wishes by including __________________ in the engagement announcement." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... someone whose engagement photo shoot involved breaking into a national park for 'the perfect shot,' ignoring all signs." },
        { type: "fill", pillar: "Compromise", text: "Our engagement almost ended when we couldn't compromise on __________________." },
        { type: "fill", pillar: "Communication", text: "The proposal would have been perfect if only their communication hadn't involved __________________." },
        { type: "fill", pillar: "Respect", text: "I knew they respected me when their proposal included a promise to never __________________." },
        { type: "thisSoundsLike", pillar: "Communication", text: "This sounds like... a proposal delivered via carrier pigeon, with a misspelled 'marry' note." },
        {
            type: "image_judgment",
            pillar: "Compromise",
            text: "What does this image say about compromise?",
            ai_prompt_description: "A photo of two people trying to assemble a piece of IKEA furniture with no instructions, one holding the manual upside down, the other trying to use a banana as a screwdriver. Stress and confusion on their faces."
        },
    ],
    "Family & Children": [
        { type: "fill", pillar: "Communication", text: "My partner and I communicate solely through __________________ when discussing bedtime routines." },
        { type: "fill", pillar: "Boundaries", text: "My in-laws always cross the boundary by __________________ every time they visit." },
        { type: "fill", pillar: "Respect", text: "Our toddler shows respect for household rules by __________________ at bedtime." },
        { type: "thisSoundsLike", pillar: "Compromise", text: "This sounds like... someone who, during a family road trip, insists on listening to their obscure polka music playlist for eight hours straight with no compromise." },
        { type: "fill", pillar: "Communication", text: "Family dinners often devolve into chaos when someone mentions __________________." },
        { type: "fill", pillar: "Boundaries", text: "We had to set a strict boundary about __________________ after the last family gathering." },
