// Component for displaying temporary messages/modals instead of alerts
const MessageModal = ({ message, onClose }) => {
    if (!message) return null;
    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-lg p-6 shadow-xl max-w-sm w-full text-center">
                <p className="text-xl font-semibold mb-4 text-gray-800">{message}</p>
                <button
                    onClick={onClose}
                    className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-6 rounded-full transition duration-300 transform hover:scale-105"
                >
                    OK
                </button>
            </div>
        </div>
    );
};

// Main App Component
function App() {
    const [userId, setUserId] = useState(null);
    const [currentPage, setCurrentPage] = useState('home'); // home, lobby, game, leaderboard, winner
    const [gameId, setGameId] = useState(null);
    const [gameData, setGameData] = useState(null);
    const [message, setMessage] = useState(null);
    const [isAuthReady, setIsAuthReady] = useState(false);
    const [isLoadingImage, setIsLoadingImage] = useState(false); // For host loading state
    const [playerName, setPlayerName] = useState(''); // New state for player nickname

    // Auth state listener and initial sign-in logic
    useEffect(() => {
        const SESSION_USER_ID_KEY = 'redFlagsGameTempUserId';

        const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
            const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null; // Corrected access to __initial_auth_token
            if (initialAuthToken) {
                // If an initial auth token is provided (e.g., from the Canvas environment)
                // Use Firebase authentication with the provided token.
                if (!currentUser) {
                    try {
                        await signInWithCustomToken(auth, initialAuthToken);
                    } catch (error) {
                        console.error("Firebase Auth Error with custom token:", error);
                        setMessage(`Authentication failed: ${error.message}. Please refresh.`);
                    }
                }
                // Once authenticated, set the user and their UID from Firebase.
                if (auth.currentUser) {
                    setUserId(auth.currentUser.uid);
                    // Set default player name if not already set, or load from storage
                    if (!playerName) {
                        setPlayerName(`Player ${auth.currentUser.uid.substring(0, 4)}`);
                    }
                }
            } else {
                // If no initial auth token (e.g., in a standalone browser tab for testing)
                // Generate and use a temporary, session-based user ID.
                let tempUserId = sessionStorage.getItem(SESSION_USER_ID_KEY);
                if (!tempUserId) {
                    tempUserId = `guest_${crypto.randomUUID()}`; // Generate a unique ID
                    sessionStorage.setItem(SESSION_USER_ID_KEY, tempUserId);
                    console.log("Generated new session userId:", tempUserId);
                } else {
                    console.log("Reusing session userId:", tempUserId);
                }

                setUserId(tempUserId); // Use the session ID as the userId
                // Set default player name for guest
                if (!playerName) {
                    setPlayerName(`Player ${tempUserId.substring(0, 5)}`);
                }
            }
            setIsAuthReady(true); // Auth listener has completed its initial check or temp ID is set
        });
        return () => unsubscribe();
    }, [playerName]); // Added playerName to dependencies to prevent re-rendering issues


    // Firebase game data listener
    useEffect(() => {
        if (!gameId || !isAuthReady || !userId) return; // Ensure auth and user are ready

        const gameDocRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
        const unsubscribeGame = onSnapshot(gameDocRef, async (docSnap) => {
            if (docSnap.exists()) {
                const data = docSnap.data();
                const oldGameData = gameData; // Capture old gameData for comparison
                setGameData(data);

                // If a new image judgment prompt was just set by the host, generate the image
                // This logic ensures image generation happens only once by the host for a new image prompt
                if (
                    userId === data.hostId && // Only host generates image
                    data.status === 'submission' && // Must be in submission phase
                    data.prompts && data.prompts[data.currentRound - 1]?.type === 'image_judgment' && // Current prompt is image type
                    !data.prompts[data.currentRound - 1]?.imageUrl && // Image not yet generated
                    (!oldGameData || oldGameData.currentRound !== data.currentRound || oldGameData.status !== 'submission') // Trigger on new round or status change to submission
                ) {
                    setIsLoadingImage(true); // Set local loading state for host
                    const currentPromptToGenerate = data.prompts[data.currentRound - 1];
                    try {
                        const imageUrl = await generateImageForPrompt(currentPromptToGenerate.ai_prompt_description);
                        // Update the Firestore document with the generated image URL for the current prompt
                        await updateDoc(gameDocRef, {
                            [`prompts.${data.currentRound - 1}.imageUrl`]: imageUrl,
                            isLoadingImage: false // Clear loading flag in Firestore
                        });
                    } catch (error) {
                        console.error("Error generating image and updating Firestore:", error);
                        await updateDoc(gameDocRef, {
                            [`prompts.${data.currentRound - 1}.imageUrl`]: "https://placehold.co/400x300/CCCCCC/000000?text=Error+Loading+Image",
                            isLoadingImage: false
                        });
                        showMessage("Failed to generate image. Please try starting the round again.");
                    } finally {
                        setIsLoadingImage(false); // Clear local loading state
                    }
                }

                // Navigate based on game status
                if (data.status === 'playing' || data.status === 'submission' || data.status === 'voting') {
                    setCurrentPage('game');
                } else if (data.status === 'round_end') {
                    setCurrentPage('leaderboard');
                } else if (data.status === 'game_over') {
                    setCurrentPage('winner');
                } else if (data.status === 'lobby') {
                    setCurrentPage('lobby');
                }
            } else {
                console.log("Game does not exist or has been deleted.");
                setGameId(null);
                setGameData(null);
                setCurrentPage('home');
                showMessage("Game not found or ended.");
            }
        }, (error) => {
            console.error("Error listening to game data:", error);
            showMessage("Failed to load game data. Please try again.");
        });

        return () => unsubscribeGame();
    }, [gameId, isAuthReady, userId, gameData]); // Keep gameData in dependencies to detect changes for image generation logic

    const generateImageForPrompt = async (promptDescription) => {
        console.log("Starting image generation for:", promptDescription);
        try {
            const payload = { instances: { prompt: promptDescription }, parameters: { "sampleCount": 1} };
            const apiKey = ""; // Canvas provides this automatically
            const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
            const response = await fetch(apiUrl, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload)
            });
            const result = await response.json();

            if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
                const imageUrl = `data:image/png;base64,${result.predictions[0].bytesBase64Encoded}`;
                console.log("Image generated:", imageUrl.substring(0, 50) + "...");
                return imageUrl;
            } else {
                console.error("AI Image generation failed: No predictions or data found.", result);
                return "https://placehold.co/400x300/CCCCCC/000000?text=Image+Generation+Failed";
            }
        } catch (apiError) {
            console.error("Error calling AI image generation API:", apiError);
            return "https://placehold.co/400x300/CCCCCC/000000?text=API+Error";
        }
    };

    const displayMessage = (msg, callback = null) => { // Renamed to avoid conflict with `message` state
        setMessage({ text: msg, callback: callback });
        // Clear message after 5 seconds if no specific callback to keep it open
        if (!callback) {
            setTimeout(() => setMessage(null), 5000);
        }
    };

    // Render based on current page
    let content;
    switch (currentPage) {
        case 'home':
            content = <HomePage setCurrentPage={setCurrentPage} setGameId={setGameId} userId={userId} showMessage={displayMessage} playerName={playerName} setPlayerName={setPlayerName} />;
            break;
        case 'lobby':
            content = <LobbyPage gameData={gameData} gameId={gameId} userId={userId} showMessage={displayMessage} />;
            break;
        case 'game':
            content = <GamePage gameData={gameData} gameId={gameId} userId={userId} showMessage={displayMessage} isLoadingImage={isLoadingImage} />; // Pass isLoadingImage
            break;
        case 'leaderboard':
            content = <LeaderboardPage gameData={gameData} userId={userId} gameId={gameId} showMessage={displayMessage} />; // Adjusted to use updated handleNextRound via prop
            break;
        case 'winner':
            content = <WinnerPage gameData={gameData} userId={userId} onPlayAgain={() => {
                setGameId(null);
                setGameData(null);
                setCurrentPage('home');
            }} showMessage={displayMessage} />; // Pass showMessage for modal
            break;
        default:
            content = <HomePage setCurrentPage={setCurrentPage} setGameId={setGameId} userId={userId} showMessage={displayMessage} playerName={playerName} setPlayerName={setPlayerName} />;
    }

    if (!isAuthReady) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-800 via-pink-600 to-red-500 text-white font-inter">
                <p className="text-3xl animate-pulse">Loading game...</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-800 via-pink-600 to-red-500 text-white font-inter flex flex-col items-center justify-center p-4">
            {message && <MessageModal message={message.text} onClose={message.callback ? () => { message.callback(); setMessage(null); } : () => setMessage(null)} />}
            <h1 className="text-5xl md:text-6xl font-extrabold text-center mb-8 drop-shadow-lg leading-tight">
                Red Flags & Relationship Goals
            </h1>
            <p className="text-lg md:text-xl text-center mb-6 text-white text-opacity-90">
                Your Player ID: <span className="font-mono bg-white bg-opacity-20 rounded-md px-3 py-1 break-all">{userId}</span>
            </p>
            <div className="w-full max-w-4xl bg-white bg-opacity-10 backdrop-filter backdrop-blur-sm rounded-3xl shadow-2xl p-6 md:p-10 border border-white border-opacity-30">
                {content}
            </div>
        </div>
    );
}

// HomePage Component
const HomePage = ({ setCurrentPage, setGameId, userId, showMessage, playerName, setPlayerName }) => {
    const [joinCode, setJoinCode] = useState('');

    const handleCreateGame = async () => {
        if (!playerName.trim()) {
            showMessage("Please enter your nickname.");
            return;
        }
        try {
            const categories = Object.keys(PROMPT_CARDS);
            const randomCategory = categories[Math.floor(Math.random() * categories.length)]; // Select a random category
            const gameCode = Math.random().toString(36).substring(2, 8).toUpperCase(); // 6-char code
            console.log("Generated Game Code:", gameCode); // Debugging log

            const initialPlayers = {};
            // Use the userId passed from App component (which is now guaranteed unique per tab)
            initialPlayers[userId] = {
                id: userId, // Added unique ID for the player object
                name: playerName.trim(), // Use custom player name
                score: 0,
                isHost: true,
                hand: shuffleArray([...ANSWER_CARDS]).slice(0, 10), // Give 10 unique cards
                hasSubmitted: false,
                hasVoted: false,
                totalRedFlags: 0,
                totalHearts: 0
            };

            const newGameRef = await addDoc(collection(db, `artifacts/${appId}/public/data/games`), {
                hostId: userId,
                category: randomCategory,
                currentRound: 1,
                maxRounds: 10,
                prompts: [], // Initialize prompts as an empty array
                status: 'lobby', // lobby, submission, voting, round_end, game_over
                players: initialPlayers,
                submittedAnswers: {},
                playerChoices: {}, // For the new game mode
                votes: {},
                gameCode: gameCode, // Ensure gameCode is always a string
                createdAt: serverTimestamp(),
                promptHistory: [], // To store played prompts text for getRandomPrompt
                answerCardPool: shuffleArray([...ANSWER_CARDS]), // All available answer cards
                isLoadingImage: false // Default to false
            });
            console.log(`Game created with Firestore path: artifacts/${appId}/public/data/games/${newGameRef.id}`); // New debugging log with full path
            setGameId(newGameRef.id);
            setCurrentPage('lobby');
            showMessage(`Game created! Share code: ${gameCode}`);
        } catch (error) {
            console.error("Error creating game:", error);
            showMessage("Failed to create game. Please try again.");
        }
    };

    const handleJoinGame = async () => {
        if (!playerName.trim()) {
            showMessage("Please enter your nickname.");
            return;
        }
        if (!joinCode) {
            showMessage("Please enter a game code.");
            return;
        }

        try {
            console.log(`Attempting to query Firestore for collection: artifacts/${appId}/public/data/games with gameCode: ${joinCode.toUpperCase()}`); // More detailed debugging log
            const q = query(collection(db, `artifacts/${appId}/public/data/games`), where("gameCode", "==", joinCode.toUpperCase()));
            const querySnapshot = await getDocs(q);
            console.log("Query Snapshot empty:", querySnapshot.empty); // Debugging log

            if (!querySnapshot.empty) {
                const gameDoc = querySnapshot.docs[0];
                const gameData = gameDoc.data();
                const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameDoc.id);

                if (gameData.players[userId]) {
                    showMessage("You are already in this game.");
                    setGameId(gameDoc.id);
                    setCurrentPage('lobby'); // Go to lobby if already in
                    return;
                }

                // If joining after game started, give initial cards
                const playerHand = shuffleArray([...ANSWER_CARDS]).slice(0, 10);

                await updateDoc(gameRef, {
                    [`players.${userId}`]: {
                        id: userId, // Added unique ID for the player object
                        name: playerName.trim(), // Use custom player name
                        score: 0,
                        isHost: false,
                        hand: playerHand,
                        hasSubmitted: false,
                        hasVoted: false,
                        totalRedFlags: 0,
                        totalHearts: 0
                    }
                });
                setGameId(gameDoc.id);
                setCurrentPage('lobby'); // Will change based on game status from listener
                showMessage(`Joined game ${joinCode}!`);
            } else {
                showMessage("Invalid game code.");
            }
        } catch (error) {
            console.error("Error joining game:", error);
            showMessage("Failed to join game. Please try again.");
        }
    };

    return (
        <div className="text-center">
            <input
                type="text"
                placeholder="Enter your nickname"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                className="w-full p-3 rounded-xl bg-white bg-opacity-20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 mb-4 text-center"
                maxLength="20" // Limit nickname length
            />
            <button
                onClick={handleCreateGame}
                className="w-full mb-4 bg-gradient-to-r from-blue-500 to-teal-400 hover:from-blue-600 hover:to-teal-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
            >
                Create New Game
            </button>
            <div className="mb-6">
                <p className="text-gray-200 mb-2">OR</p>
                <input
                    type="text"
                    placeholder="Enter Game Code"
                    value={joinCode}
                    onChange={(e) => setJoinCode(e.target.value)}
                    className="w-full p-3 rounded-xl bg-white bg-opacity-20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 mb-3 text-center uppercase"
                    maxLength="6"
                />
                <button
                    onClick={handleJoinGame}
                    className="w-full bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                >
                    Join Game
                </button>
            </div>
            <p className="text-sm text-gray-300 mt-4">
                Minimum 2 players needed to start a game.
            </p>
        </div>
    );
};

// LobbyPage Component
const LobbyPage = ({ gameData, gameId, userId, showMessage }) => {
    if (!gameData) return <p className="text-center text-xl text-gray-200">Loading lobby...</p>;

    const players = Object.values(gameData.players || {}).sort((a, b) => a.name.localeCompare(b.name));
    const isHost = gameData.hostId === userId;
    const canStartGame = players.length >= 2;
    const gameCode = gameData.gameCode;

    const handleCategoryChange = async (e) => {
        if (!isHost) return;
        const newCategory = e.target.value;
        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            await updateDoc(gameRef, { category: newCategory });
        } catch (error) {
            console.error("Error updating category:", error);
            showMessage("Failed to update category.");
        }
    };

    const handleStartGame = async () => {
        if (!isHost) return;
        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            const playersInGame = Object.values(gameData.players);
            if (playersInGame.length < 1) { // Changed from 2 to 1 to allow solo testing
                showMessage("You need at least 1 player to start the game.");
                return;
            }

            // Distribute unique hands to all players
            const promptCardsForCategory = PROMPT_CARDS[gameData.category];
            let availableCards = [...ANSWER_CARDS];
            const updatedPlayers = { ...gameData.players }; // Create a mutable copy

            for (const pId in updatedPlayers) {
                const hand = [];
                // Ensure players always have 10 cards at the start of the game
                for (let i = 0; i < 10; i++) {
                    if (availableCards.length === 0) {
                        // If pool runs low, replenish it with all ANSWER_CARDS shuffled
                        availableCards = shuffleArray([...ANSWER_CARDS]);
                        console.warn("Answer card pool replenished by reshuffling all cards during initial deal.");
                    }
                    const cardIndex = Math.floor(Math.random() * availableCards.length);
                    hand.push(availableCards.splice(cardIndex, 1)[0]);
                }
                updatedPlayers[pId].hand = hand;
                updatedPlayers[pId].hasSubmitted = false;
                updatedPlayers[pId].hasVoted = false;
            }

            const initialPrompt = getRandomPrompt(promptCardsForCategory, []); // No history yet for first prompt
            
            // Image generation now happens in the onSnapshot listener triggered by the update below
            // No direct image generation call here.

            await updateDoc(gameRef, {
                status: 'submission',
                currentRound: 1,
                maxRounds: Math.min(10, promptCardsForCategory.length), // Set max rounds
                prompts: [initialPrompt], // Store the full prompt object
                promptHistory: [initialPrompt.text], // Keep text history for getRandomPrompt filter
                players: updatedPlayers, // CRITICAL FIX: Pass updatedPlayers to Firestore
                submittedAnswers: {},
                playerChoices: {},
                votes: {},
                isLoadingImage: initialPrompt.type === 'image_judgment' // Set initial loading flag for image prompt
            });

        } catch (error) {
            console.error("Error starting game:", error);
            showMessage("An error occurred while starting the game.");
        }
    };

    return (
        <div className="text-center p-4">
            <h2 className="text-4xl font-bold mb-4 text-white">Game Lobby</h2>
            <p className="text-xl text-gray-200 mb-6">
                Game Code: <span className="font-bold text-yellow-300">{gameCode}</span>
            </p>
            <div className="text-lg text-gray-200 mb-2 flex items-center justify-center">
                <span className="mr-2">Category:</span>
                {isHost ? (
                    <select
                        value={gameData.category}
                        onChange={handleCategoryChange}
                        className="p-2 rounded-lg bg-white bg-opacity-20 text-white font-semibold focus:outline-none focus:ring-2 focus:ring-blue-400"
                    >
                        {Object.keys(PROMPT_CARDS).map(cat => (
                            <option key={cat} value={cat} className="text-black">{cat}</option>
                        ))}
                    </select>
                ) : (
                    <span className="font-semibold text-pink-200">{gameData.category}</span>
                )}
            </div>
            <p className="text-lg text-gray-200 mb-4">Players Connected:</p>
            <ul className="list-disc list-inside text-left mx-auto max-w-xs mb-8 text-gray-100">
                {players.map(player => (
                    <li key={player.id} className="py-1">
                        {player.name} {player.isHost && "(Host)"}
                    </li>
                ))}
            </ul>
            {isHost && (
                <button
                    onClick={handleStartGame}
                    disabled={!canStartGame}
                    className={`w-full py-4 px-8 rounded-full shadow-lg transition duration-300 transform ${
                        canStartGame
                            ? 'bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold hover:scale-105'
                            : 'bg-gray-600 text-gray-300 cursor-not-allowed'
                    }`}
                >
                    {canStartGame ? 'Start Game' : `Need ${2 - players.length} more player(s) to start`}
                </button>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Waiting for host to start the game!</p>
            )}
        </div>
    );
};

// GamePage Component
const GamePage = ({ gameData, gameId, userId, showMessage, isLoadingImage }) => { // Pass isLoadingImage
    if (!gameData || !gameData.prompts || gameData.prompts.length === 0) {
        return <p className="text-center text-xl text-gray-200">Waiting for game to start...</p>;
    }
    const currentPrompt = gameData.prompts[gameData.currentRound - 1];

    if (!currentPrompt) {
        return <p className="text-center text-xl text-gray-200">Loading prompt data...</p>;
    }

    return <GameRound gameData={gameData} gameId={gameId} userId={userId} showMessage={showMessage} isLoadingImage={isLoadingImage} />; // Pass isLoadingImage
};

const GamePrompt = ({ prompt, gameData, isLoadingImage }) => { // Receive isLoadingImage
    if (!prompt) return null;

    // Show loading message if it's an image prompt AND host is currently generating it
    if (prompt.type === 'image_judgment' && isLoadingImage) { // Check local loading state
        return (
            <div className="text-center p-4">
                <h2 className="text-3xl font-bold mb-4 text-white animate-pulse">Generating AI Image...</h2>
                <p className="text-xl text-gray-200">The host is creating a new visual prompt. Please wait.</p>
            </div>
        );
    }

    return (
        <div className="text-center bg-white bg-opacity-20 rounded-xl p-4 md:p-6 shadow-xl w-full max-w-2xl border border-white border-opacity-30 mb-6">
            <p className="text-xl font-bold text-yellow-200 mb-2">{prompt.pillar}</p>
            <p className="text-3xl md:text-4xl font-extrabold text-white leading-tight"
                dangerouslySetInnerHTML={{ __html: prompt.text.replace(/__________________/g, '<span class="text-pink-300 underline">BLANK</span>') }}
            >
            </p>
            {prompt.type === 'image_judgment' && prompt.imageUrl && (
                <img src={prompt.imageUrl} alt={prompt.ai_prompt_description} className="mt-4 rounded-lg shadow-lg mx-auto w-full max-w-md h-auto object-contain" />
            )}
            <p className="mt-4 text-lg text-gray-200">
                Round {gameData.currentRound} of {gameData.maxRounds}
            </p>
        </div>
    );
};

const GameRound = ({ gameData, gameId, userId, showMessage, isLoadingImage }) => { // Receive isLoadingImage
    // State for player's current action
    const [selectedCard, setSelectedCard] = useState(null);
    const [customAnswer, setCustomAnswer] = useState('');
    const [nominationData, setNominationData] = useState({ nominatedPlayerId: '', justification: '' });
    const [selectedVote, setSelectedVote] = useState(null);
    const [selectedChoice, setSelectedChoice] = useState(null); // For scenario_vote

    const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
    const currentPlayer = gameData.players[userId];
    const { players: allPlayers, currentRound, status, prompts, submittedAnswers: submittedAnswersObj } = gameData;
    const currentPrompt = prompts[currentRound - 1]; // Access the correct prompt based on round
    const isHost = gameData.hostId === userId;

    const submittedAnswers = Object.values(submittedAnswersObj || {}).sort((a, b) => (a.submittedAt?.seconds || 0) - (b.submittedAt?.seconds || 0));
    const playersInGame = Object.values(allPlayers || {}).sort((a, b) => a.name.localeCompare(b.name));

    // Reset local state when game phase changes
    useEffect(() => {
        setSelectedCard(null);
        setCustomAnswer('');
        setNominationData({ nominatedPlayerId: '', justification: '' });
        setSelectedVote(null);
        setSelectedChoice(null);
    }, [gameData.status, gameData.currentRound]);

    const handleSelectCard = (card) => {
        setSelectedCard(card);
    };

    const handleCustomAnswerChange = (e) => {
        setCustomAnswer(e.target.value);
    };

    const handleNominationChange = (field, value) => {
        setNominationData(prev => ({ ...prev, [field]: value }));
    };

    const handleSubmitAnswer = async () => {
        if (currentPlayer.hasSubmitted) {
            showMessage("You have already submitted an answer for this round.");
            return;
        }

        // If it's an image judgment prompt and the image hasn't loaded yet, prevent submission
        if (currentPrompt.type === 'image_judgment' && !currentPrompt.imageUrl && isLoadingImage) {
            showMessage("Please wait for the image to finish generating before submitting.");
            return;
        }

        let answerContent = '';
        // For fill and image_judgment, answer is a card or custom text
        if (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') {
            if (selectedCard === null) {
                showMessage("Please select a card or type a custom answer.");
                return;
            }
            if (selectedCard === "BLANK_CARD_SELECTED") {
                if (!customAnswer.trim()) {
                    showMessage("Please type your custom answer.");
                    return;
                }
                answerContent = customAnswer.trim();
            } else {
                answerContent = selectedCard;
            }
        } else if (currentPrompt.type === 'thisSoundsLike') {
            if (!nominationData.nominatedPlayerId || !nominationData.justification.trim()) {
                showMessage("Please select a player and provide a justification.");
                return;
            }
            answerContent = {
                nominatedPlayerId: nominationData.nominatedPlayerId,
                justification: nominationData.justification.trim()
            };
        } else {
            showMessage("Invalid prompt type.");
            return;
        }

        try {
            const submissionId = generateId();
            const updates = {
                [`players.${userId}.hasSubmitted`]: true,
                // Only remove the card if it was selected from the hand and not a custom answer
                [`players.${userId}.hand`]: (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && selectedCard !== "BLANK_CARD_SELECTED"
                    ? currentPlayer.hand.filter(card => card !== selectedCard)
                    : currentPlayer.hand,
                [`submittedAnswers.${submissionId}`]: {
                    id: submissionId,
                    playerId: userId,
                    answer: answerContent,
                    votes: 0,
                    voters: [],
                    submittedAt: serverTimestamp() // Add timestamp for stable sorting
                }
            };

            await updateDoc(gameRef, updates);
            showMessage("Answer submitted!");
        } catch (error) {
            console.error("Error submitting answer:", error);
            showMessage("Failed to submit answer. Please try again.");
        }
    };

    const handleSubmitChoice = async () => {
        if (!selectedChoice) {
            showMessage("Please select a choice.");
            return;
        }
        try {
            const updates = {
                [`players.${userId}.hasSubmitted`]: true,
                [`playerChoices.${userId}`]: selectedChoice
            };
            await updateDoc(gameRef, updates);
            showMessage("Your choice has been submitted!");
        } catch (error) {
            console.error("Error submitting choice:", error);
            showMessage("Failed to submit choice. Please try again.");
        }
    };

    // Check if all active players have submitted their answers
    const submittedCount = currentPrompt.type === 'scenario_vote'
        ? Object.keys(gameData.playerChoices || {}).length
        : submittedAnswers.length;

    const playersWhoShouldSubmit = playersInGame.filter(p => !p.isHost || currentPrompt.type !== 'fill' && currentPrompt.type !== 'image_judgment');
    const allPlayersSubmitted = submittedCount === playersWhoShouldSubmit.length;

    // Host manages phase transitions
    useEffect(() => {
        // Only trigger if current phase is 'submission' and all relevant players have submitted
        if (isHost && gameData.status === 'submission' && allPlayersSubmitted && playersWhoShouldSubmit.length > 0) {
            updateDoc(gameRef, { status: 'voting' }).catch(e => console.error("Error setting status to voting:", e));
        }
    }, [allPlayersSubmitted, playersWhoShouldSubmit.length, isHost, gameData.status, gameRef]);


    const handleScenarioVote = async (targetPlayerId, voteType) => {
        if (currentPlayer.hasVoted) {
            showMessage("You have already submitted your votes for this round.");
            return;
        }

        // Prevent voting for self for scenario_vote as well
        if (targetPlayerId === userId) {
            showMessage("You cannot vote for your own take.");
            return;
        }

        const votePath = `votes.${targetPlayerId}.${voteType}`;
        try {
            await updateDoc(gameRef, {
                [votePath]: arrayUnion(userId)
            });
            showMessage(`${voteType === 'hearts' ? 'Heart' : 'Red Flag'} given!`);
        } catch (error) {
            console.error("Error casting scenario vote:", error);
            showMessage("Failed to cast vote.");
        }
    };

    const handleLockInVotes = async () => {
        if (currentPlayer.hasVoted) return;
        try {
            await updateDoc(gameRef, { [`players.${userId}.hasVoted`]: true });
            showMessage("Votes locked in!");
        } catch (error) {
            console.error("Error locking in votes:", error);
            showMessage("Failed to lock in votes.");
        }
    };

    const handleVote = async (submissionId) => {
        if (currentPlayer.hasVoted) {
            showMessage("You've already voted this round.");
            return;
        }
        if (selectedVote) {
            showMessage("You can only vote for one answer.");
            return;
        }

        const submission = submittedAnswersObj[submissionId]; // Use submittedAnswersObj directly
        if (!submission) return;

        // Prevent voting for oneself in applicable modes
        if (submission.playerId === userId) {
            showMessage("You cannot vote for your own answer.");
            return;
        }

        setSelectedVote(submissionId); // Optimistically update UI

        try {
            await updateDoc(gameRef, {
                [`submittedAnswers.${submissionId}.votes`]: increment(1),
                [`submittedAnswers.${submissionId}.voters`]: arrayUnion(userId),
                [`players.${userId}.hasVoted`]: true,
                [`votes.${submissionId}`]: arrayUnion(userId) // General vote tracking
            });
            showMessage("Vote submitted!");
        } catch (error) {
            console.error("Error submitting vote:", error);
            setSelectedVote(null); // Revert on error
            showMessage("Failed to submit vote. Please try again.");
        }
    };

    // Determine if all players have voted
    const votersCount = playersInGame.filter(p => p.hasVoted).length;
    const allPlayersVoted = votersCount === playersInGame.length;


    useEffect(() => {
        // Only trigger if we are in the voting phase and all active players have voted
        if (isHost && gameData.status === 'voting' && allPlayersVoted && playersInGame.length > 0) {
            
            const updates = {};
            let roundResults = {}; // For scenario_vote display

            if (currentPrompt.type === 'scenario_vote') {
                playersInGame.forEach(player => {
                    const votesOnPlayer = gameData.votes[player.id] || {};
                    const hearts = (votesOnPlayer.hearts || []).length;
                    const redFlags = (votesOnPlayer.redFlags || []).length;
                    const roundScore = hearts - redFlags;
                    
                    updates[`players.${player.id}.score`] = increment(roundScore);
                    updates[`players.${player.id}.totalHearts`] = increment(hearts);
                    updates[`players.${player.id}.totalRedFlags`] = increment(redFlags);

                    roundResults[player.id] = {
                        choiceKey: gameData.playerChoices[player.id],
                        hearts,
                        redFlags
                    };
                });
            } else {
                // Existing scoring logic for other modes
                let maxVotes = 0;
                let winningSubmissions = [];

                submittedAnswers.forEach(sub => {
                    if (sub.votes > maxVotes) {
                        maxVotes = sub.votes;
                        winningSubmissions = [sub];
                    } else if (sub.votes === maxVotes) {
                        winningSubmissions.push(sub);
                    }
                });

                if (maxVotes > 0) {
                    winningSubmissions.forEach(sub => {
                        updates[`players.${sub.playerId}.score`] = increment(1);
                    });
                }
                // Store winning submission IDs for retrieval in LeaderboardPage
                updates.currentWinningSubmission = maxVotes > 0 ? winningSubmissions.map(s => s.id) : null;
            }

            updates.status = 'round_end';
            if (currentPrompt.type === 'scenario_vote') {
                updates.roundResults = roundResults;
            }

            updateDoc(gameRef, updates).catch(e => console.error("Error ending round:", e));
        }
    }, [allPlayersVoted, playersInGame.length, isHost, gameData.status, gameRef, submittedAnswers, currentPrompt]);

    const playerHand = currentPlayer?.hand || [];

    return (
        <div className="w-full max-w-4xl mx-auto p-4 flex flex-col items-center justify-center">
            <GamePrompt prompt={currentPrompt} gameData={gameData} isLoadingImage={isLoadingImage} /> {/* Pass isLoadingImage */}

            {gameData.status === 'submission' && (
                currentPrompt.type === 'scenario_vote' ? (
                    <div className="w-full text-center">
                        {!currentPlayer.hasSubmitted ? (
                            <>
                                <h3 className="text-2xl font-bold text-white mb-4">What's your take?</h3>
                                <div className="space-y-3 mb-6">
                                    {Object.entries(currentPrompt.choices).sort((a, b) => a[0].localeCompare(b[0])).map(([key, value]) => (
                                        <button
                                            key={key}
                                            onClick={() => setSelectedChoice(key)}
                                            className={`w-full text-left p-4 rounded-xl shadow-md transition transform ${
                                                selectedChoice === key ? 'bg-blue-400 scale-105 ring-4 ring-blue-500 text-white' : 'bg-white hover:bg-gray-100 text-gray-900'
                                            }`}
                                        >
                                            <span className="font-bold uppercase mr-3">{key})</span>{value}
                                        </button>
                                    ))}
                                </div>
                                <button
                                    onClick={handleSubmitChoice}
                                    disabled={!selectedChoice}
                                    className="w-full bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 disabled:opacity-50"
                                >
                                    Submit Choice
                                </button>
                            </>
                        ) : (
                            <p className="text-2xl text-gray-200 animate-pulse">Waiting for other players to submit their take...</p>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Submitted: {submittedCount} / {playersWhoShouldSubmit.length}
                        </p>
                    </div>
                ) : (
                    <div className="w-full max-w-lg mx-auto">
                        {!currentPlayer.hasSubmitted ? (
                            <>
                                <h3 className="text-xl font-bold text-white mb-4">Your Hand:</h3>
                                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 mb-6">
                                    {playerHand.map((card, index) => (
                                        <button
                                            key={card + index} // Use card content + index as key for uniqueness
                                            onClick={() => handleSelectCard(card)}
                                            className={`p-3 rounded-lg shadow-md h-32 text-sm text-gray-900 transition-transform transform hover:scale-105 ${selectedCard === card ? 'bg-cyan-300 ring-4 ring-cyan-500' : 'bg-white hover:bg-gray-100'}`}
                                        >
                                            {card}
                                        </button>
                                    ))}
                                    {(currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && (
                                        <button
                                            onClick={() => handleSelectCard("BLANK_CARD_SELECTED")}
                                            className={`p-3 rounded-lg shadow-md h-32 text-sm transition-transform transform hover:scale-105 ${selectedCard === "BLANK_CARD_SELECTED" ? 'bg-yellow-300 ring-4 ring-yellow-500 text-gray-900' : 'bg-gray-200 hover:bg-gray-300 text-gray-900'}`}
                                        >
                                            Write your own...
                                        </button>
                                    )}
                                </div>

                                {currentPrompt.type === 'thisSoundsLike' && (
                                    <div className="mb-4">
                                        <h3 className="text-xl font-bold text-white mb-2">1. Nominate a Player</h3>
                                        <select
                                            value={nominationData.nominatedPlayerId}
                                            onChange={(e) => setNominationData(prev => ({ ...prev, nominatedPlayerId: e.target.value }))}
                                            className="w-full p-3 rounded-lg bg-white text-gray-900"
                                        >
                                            <option value="">Select a player...</option>
                                            {playersInGame.filter(p => p.id !== userId).map(p => (
                                                <option key={p.id} value={p.id}>{p.name}</option>
                                            ))}
                                        </select>
                                        <h3 className="text-xl font-bold text-white mb-2 mt-4">2. Justify your nomination</h3>
                                        <textarea
                                            value={nominationData.justification}
                                            onChange={(e) => setNominationData(prev => ({ ...prev, justification: e.target.value }))}
                                            placeholder="Why does this sound like them?"
                                            className="w-full p-3 rounded-lg bg-white text-gray-900 h-24"
                                        ></textarea>
                                    </div>
                                )}

                                {selectedCard === "BLANK_CARD_SELECTED" && (
                                    <div className="mb-4">
                                        <h3 className="text-xl font-bold text-white mb-2">Write Your Custom Answer</h3>
                                        <textarea
                                            value={customAnswer}
                                            onChange={(e) => setCustomAnswer(e.target.value)}
                                            className="w-full p-3 rounded-lg bg-white text-gray-900 h-24"
                                            placeholder="Make it good!"
                                        ></textarea>
                                    </div>
                                )}
                                <button
                                    onClick={handleSubmitAnswer}
                                    className="w-full bg-gradient-to-r from-teal-500 to-cyan-600 hover:from-teal-600 hover:to-cyan-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                                    disabled={((currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && (!selectedCard || (selectedCard === "BLANK_CARD_SELECTED" && !customAnswer.trim()))) || (currentPrompt.type === 'thisSoundsLike' && (!nominationData.nominatedPlayerId || !nominationData.justification.trim()))}
                                >
                                    Submit Answer
                                </button>
                            </>
                        ) : (
                            <p className="text-2xl text-gray-200 animate-pulse">Waiting for other players to submit...</p>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Submitted: {submittedCount} / {playersWhoShouldSubmit.length}
                        </p>
                    </div>
                )
            )}

            {gameData.status === 'voting' && (
                currentPrompt.type === 'scenario_vote' ? (
                    <div className="w-full text-center">
                        <h3 className="text-2xl font-bold text-white mb-4">Judge Their Takes!</h3>
                        <p className="text-gray-300 mb-6">Give a ❤️ for a relationship goal or a 🚩 for a red flag.</p>
                        <div className="space-y-4 mb-6">
                            {playersInGame.filter(p => p.id !== userId).map(player => { // Filter self from voting
                                const choiceKey = gameData.playerChoices[player.id];
                                const choiceText = choiceKey ? currentPrompt.choices[choiceKey] : "No choice submitted.";
                                const hearts = gameData.votes?.[player.id]?.hearts || [];
                                const redFlags = gameData.votes?.[player.id]?.redFlags || [];
                                const hasVotedHeart = hearts.includes(userId);
                                const hasVotedFlag = redFlags.includes(userId);

                                return (
                                    <div key={player.id} className="bg-white bg-opacity-20 rounded-xl p-4 text-left">
                                        <p className="font-bold text-white text-lg">{player.name}'s take:</p>
                                        <p className="text-gray-200 mb-3">"{choiceText}"</p>
                                        <div className="flex justify-end items-center space-x-3">
                                            <button
                                                onClick={() => handleScenarioVote(player.id, 'hearts')}
                                                disabled={hasVotedHeart || hasVotedFlag || currentPlayer.hasVoted}
                                                className={`px-4 py-2 rounded-full font-bold text-2xl transition transform hover:scale-110 disabled:opacity-50 ${hasVotedHeart ? 'bg-pink-400' : 'bg-gray-300 hover:bg-pink-300'}`}
                                            >
                                                ❤️
                                            </button>
                                            <button
                                                onClick={() => handleScenarioVote(player.id, 'redFlags')}
                                                disabled={hasVotedHeart || hasVotedFlag || currentPlayer.hasVoted}
                                                className={`px-4 py-2 rounded-full font-bold text-2xl transition transform hover:scale-110 disabled:opacity-50 ${hasVotedFlag ? 'bg-red-500' : 'bg-gray-300 hover:bg-red-400'}`}
                                            >
                                                🚩
                                            </button>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                        {!currentPlayer.hasVoted && (
                            <button onClick={handleLockInVotes} className="w-full bg-gradient-to-r from-green-500 to-lime-400 text-white font-bold py-4 px-8 rounded-full shadow-lg">
                                Lock In Your Votes
                            </button>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Voted: {votersCount} / {playersInGame.length}
                        </p>
                    </div>
                ) : (
                    <div className="w-full text-center">
                        <h3 className="text-2xl font-bold text-white mb-4">Vote for your favorite!</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                            {submittedAnswers.map(sub => (
                                <div key={sub.id} className="relative bg-white bg-opacity-20 rounded-xl p-4 flex flex-col justify-between">
                                    <p className="text-white text-lg font-semibold mb-4">"{sub.answer}"</p>
                                    <div className="text-right">
                                        <button
                                            onClick={() => handleVote(sub.id)}
                                            disabled={currentPlayer.hasVoted || sub.playerId === userId}
                                            className="bg-gradient-to-r from-pink-500 to-orange-500 hover:from-pink-600 hover:to-orange-600 text-white font-bold py-2 px-4 rounded-full shadow-lg transition duration-300 disabled:opacity-50"
                                        >
                                            Vote
                                        </button>
                                        {gameData.votes[sub.id] && <span className="ml-4 text-lg font-bold text-white">{(gameData.votes[sub.id] || []).length}</span>} {/* Corrected vote count display */}
                                    </div>
                                </div>
                            ))}
                        </div>
                        {!currentPlayer.hasVoted && (
                            <p className="text-lg text-gray-300">Choose the funniest or most fitting answer!</p>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Voted: {votersCount} / {playersInGame.length}
                        </p>
                    </div>
                )
            )}
        </div>
    );
};

// LeaderboardPage Component
const LeaderboardPage = ({ gameData, userId, gameId, showMessage }) => {
    const isHost = gameData.hostId === userId;
    const players = Object.values(gameData.players).sort((a, b) => b.score - a.score);
    const currentPrompt = gameData.prompts[gameData.currentRound - 1]; // Access the correct prompt

    const winningSubmissions = (gameData.currentWinningSubmission || []).map(id => gameData.submittedAnswers[id]).filter(Boolean);
    const winnerNames = winningSubmissions.map(sub => gameData.players[sub.playerId]?.name).join(', ');

    const handleNextRound = async () => {
        if (!isHost) return;

        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            const nextRound = gameData.currentRound + 1;

            if (nextRound > gameData.maxRounds) {
                await updateDoc(gameRef, { status: 'game_over' });
            } else {
                const promptCardsForCategory = PROMPT_CARDS[gameData.category];
                const newPrompt = getRandomPrompt(promptCardsForCategory, gameData.promptHistory || []);
                
                // Reset player states for the new round and replenish hands
                const updatedPlayers = {};
                let currentAnswerCardPool = [...gameData.answerCardPool]; // Make a mutable copy

                for (const pId in gameData.players) {
                    const player = gameData.players[pId];
                    let currentHand = player.hand || [];
                    const cardsNeeded = 10 - currentHand.length; // Ensure hand size is 10

                    let newCards = [];
                    if (cardsNeeded > 0) {
                        if (currentAnswerCardPool.length < cardsNeeded) {
                            // If pool runs low, replenish it with all ANSWER_CARDS shuffled
                            currentAnswerCardPool = shuffleArray([...ANSWER_CARDS]);
                            console.warn("Answer card pool replenished by reshuffling all cards.");
                        }
                        newCards = currentAnswerCardPool.slice(0, cardsNeeded);
                        currentAnswerCardPool = currentAnswerCardPool.slice(cardsNeeded);
                    }

                    updatedPlayers[pId] = {
                        ...player,
                        hand: [...currentHand, ...newCards], // Replenish hand
                        hasSubmitted: false,
                        hasVoted: false
                    };
                }

                await updateDoc(gameRef, {
                    status: 'submission',
                    currentRound: nextRound,
                    prompts: arrayUnion(newPrompt), // Store the full new prompt object
                    promptHistory: arrayUnion(newPrompt.text), // Only store text for history check
                    players: updatedPlayers,
                    submittedAnswers: {}, // Clear submissions for new round
                    playerChoices: {}, // Clear player choices for new round
                    votes: {}, // Clear votes for new round
                    roundResults: deleteField(), // Clean up old round results
                    currentWinningSubmission: null, // Clear winning submission from previous round
                    answerCardPool: currentAnswerCardPool, // Save the updated pool state
                    isLoadingImage: newPrompt.type === 'image_judgment' // Set loading flag for host to generate image
                });
            }
        } catch (error) {
            console.error("Error advancing round:", error);
            showMessage("Failed to advance round. Please try again.");
        }
    };

    return (
        <div className="text-center p-4">
            <h2 className="text-4xl font-bold text-white mb-6 animate-fade-in-down">Round {gameData.currentRound} Results</h2>

            {currentPrompt.type === 'scenario_vote' && gameData.roundResults ? (
                 <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 mb-8 w-full max-w-2xl mx-auto">
                    <h3 className="text-2xl font-bold text-cyan-300 mb-4">How did everyone do?</h3>
                    <div className="space-y-4">
                        {players.map(player => {
                            const result = gameData.roundResults[player.id];
                            if (!result) return null;
                            const choiceText = currentPrompt.choices[result.choiceKey];
                            return (
                                <div key={player.id} className="text-left p-4 bg-gray-900 bg-opacity-50 rounded-lg">
                                    <p className="font-bold text-white text-xl">{player.name}'s take:</p>
                                    <p className="text-gray-300 italic my-2">"{choiceText}"</p>
                                    <div className="flex justify-end items-center space-x-4 text-xl">
                                        <span className="text-pink-400 font-bold">❤️ {result.hearts}</span>
                                        <span className="text-red-500 font-bold">🚩 {result.redFlags}</span>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>
            ) : (
                <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 mb-8 w-full max-w-2xl mx-auto">
                    <h3 className="text-2xl font-bold text-cyan-300 mb-2">Winning Answer{winningSubmissions.length > 1 ? 's' : ''}</h3>
                    {winningSubmissions.length > 0 ? (
                        <>
                            {winningSubmissions.map(sub => (
                                <p key={sub.id} className="text-white text-xl font-semibold bg-gray-900 bg-opacity-50 rounded-md p-4 my-2">"{sub.answer}"</p>
                            ))}
                            <p className="text-gray-300 mt-2 text-lg">Submitted by: <span className="font-bold text-white">{winnerNames}</span> (+1 point)</p>
                        </>
                    ) : (
                        <p className="text-white text-xl font-semibold">No winning answer this round. Everyone gets to keep their dignity.</p>
                    )}
                </div>
            )}


            <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 w-full max-w-md mx-auto">
            <h3 className="text-3xl font-bold text-white mb-4">Leaderboard</h3>
            <ul className="space-y-3 mb-8">
                {players.map((player, index) => (
                    <li key={player.id} className={`flex items-center justify-between p-4 rounded-xl shadow-md ${
                        index === 0 ? 'bg-yellow-300 text-gray-900 font-bold text-xl' : 'bg-white bg-opacity-30 text-white'
                    }`}>
                        <span className="flex items-center">
                            {index === 0 && <span className="mr-2 text-2xl">👑</span>}
                            {player.name}
                        </span>
                        <span className="font-bold text-xl">{player.score} {player.score === 1 ? 'Point' : 'Points'}</span>
                    </li>
                ))}
            </ul>

            {isHost && (
                <button
                    onClick={handleNextRound}
                    className="w-full bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                >
                    {gameData.currentRound < gameData.maxRounds ? 'Next Round' : 'End Game'}
                </button>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Waiting for host to continue...</p>
            )}
            </div>
        </div>
    );
};

// WinnerPage Component
const WinnerPage = ({ gameData, userId, showMessage, onPlayAgain }) => { // Added showMessage and onPlayAgain for delete logic
    if (!gameData || !gameData.players) return <p className="text-center text-xl text-gray-200">Calculating winner...</p>;

    const players = Object.values(gameData.players).sort((a, b) => b.score - a.score);
    const winner = players[0];
    const isHost = gameData.hostId === userId;

    const getPlayerTitle = (player) => {
        if ((player.totalHearts + player.totalRedFlags) > 0) {
            return player.totalHearts >= player.totalRedFlags ? "Lover" : "Walking Red Flag";
        }
        return "Still Figuring it Out";
    };

    const handleDeleteGame = async () => {
        showMessage("Are you sure you want to delete this game? This action cannot be undone.", async () => {
            try {
                // Ensure gameData.id is available. It should be, as this is WinnerPage.
                // Firebase doc ID is automatically populated on gameData from onSnapshot
                if (gameData.id) {
                    await deleteDoc(doc(db, `artifacts/${appId}/public/data/games`, gameData.id));
                    onPlayAgain(); // Go back to home page after deletion
                } else {
                    console.error("Game ID is undefined, cannot delete.");
                    showMessage("Failed to delete game: Game ID missing.");
                }
            } catch (error) {
                console.error("Error deleting game:", error);
                showMessage("Failed to delete game. Please try again.");
            }
        });
    };


    return (
        <div className="text-center p-4">
            <h2 className="text-5xl font-extrabold text-yellow-300 mb-6 drop-shadow-lg">
                Game Over!
            </h2>
            <p className="text-3xl font-bold text-white mb-8">
                The Champion of {gameData.category || "Relationships"} is... <span className="font-bold text-cyan-400">{winner.name}!</span>
            </p>

            <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 w-full max-w-2xl mx-auto">
                <h4 className="text-2xl font-bold text-white mb-4">Final Standings</h4>
                <ul className="space-y-4">
                    {players.map((player, index) => (
                        <li key={player.id} className="flex justify-between items-center p-4 bg-gray-900 bg-opacity-50 rounded-lg text-white">
                            <div className="flex items-center">
                                <span className={`text-2xl font-bold mr-4 ${index === 0 ? 'text-yellow-400' : ''}`}>{index + 1}.</span>
                                <div>
                                    <p className="text-xl font-semibold">{player.name}</p>
                                    <p className="text-sm text-cyan-300">{getPlayerTitle(player)}</p>
                                </div>
                            </div>
                            <span className="text-2xl font-bold">{player.score} pts</span>
                        </li>
                    ))}
                </ul>
            </div>

            {isHost && (
                <>
                    <button
                        onClick={onPlayAgain}
                        className="w-full bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 mb-4"
                    >
                        Play Again!
                    </button>
                    <button
                        onClick={handleDeleteGame}
                        className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                    >
                        Delete This Game
                    </button>
                </>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Ask the host to start a new game!</p>
            )}
        </div>
    );
};

// Utility function to shuffle an array (Fisher-Yates)
const shuffleArray = (array) => {
    let currentIndex = array.length, randomIndex;
    while (currentIndex !== 0) {
        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex--;
        [array[currentIndex], array[randomIndex]] = [array[randomIndex], array[currentIndex]];
    }
    return array;
};

// Utility function to get a random prompt that hasn't been played yet
const getRandomPrompt = (promptList, history) => {
    // History should contain the 'text' of previously played prompts
    const availablePrompts = promptList.filter(p => !history.includes(p.text));
    if (availablePrompts.length === 0) {
        console.warn("All prompts for this category have been used. Reshuffling prompts for this round.");
        return promptList[Math.floor(Math.random() * promptList.length)]; // If all used, pick randomly from full list
    }
    const randomIndex = Math.floor(Math.random() * availablePrompts.length);
    return availablePrompts[randomIndex];
};

export default App;
