import React, { useState, useEffect, useRef, useMemo } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, getDoc, addDoc, setDoc, updateDoc, deleteDoc, onSnapshot, collection, query, where, arrayUnion, arrayRemove, serverTimestamp, getDocs, deleteField, increment } from 'firebase/firestore';
import PlayerProfile from './PlayerProfile';

// Firebase configuration from environment variables
const firebaseConfig = JSON.parse(process.env.REACT_APP_FIREBASE_CONFIG || '{}');
const appId = process.env.REACT_APP_ID || 'default-app-id';

// Initialize Firebase outside the component to avoid re-initialization
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// Helper for generating unique IDs (not cryptographically secure, but fine for game items)
const generateId = () => Math.random().toString(36).substring(2, 11);

// Pre-defined game data
const PROMPT_CARDS = {
    "Red Flag or Relationship Goal?": [], // This will now be dynamically generated
    "Love, Laugh, or Leave?": [
        { type: "fill", pillar: "Boundaries", text: "My date set a clear boundary by stating they wouldn't discuss anything before __________________." },
        { type: "fill", pillar: "Communication", text: "I knew there would be communication challenges when their opening line was, 'So, do you believe in __________________?'" },
        { type: "fill", pillar: "Respect", text: "My date showed a shocking lack of respect when they started __________________ mid-conversation." },
        { type: "thisSoundsLike", pillar: "Compromise", text: "This sounds like... someone who, on a first date, orders for both of you and then says, 'No compromise on the appetizers!'" },
        { type: "fill", pillar: "Boundaries", text: "The biggest red flag on our first date was when they instantly started talking about __________________." },
        { type: "fill", pillar: "Communication", text: "Their idea of 'getting to know you' involved asking about my __________________." },
        { type: "fill", pillar: "Respect", text: "I lost all respect for them when they pulled out a __________________ at the table." },
        { type: "thisSoundsLike", pillar: "Boundaries", text: "This sounds like... someone who brings their ex to a first date as a 'wingman'." },
        {
            type: "image_judgment",
            pillar: "Boundaries",
            text: "What's your red flag reaction to this scene?",
            ai_prompt_description: "A photo of a person setting up a large, elaborate, and overly aggressive 'no trespassing' sign with barbed wire around a small, innocent-looking flower bed."
        },
    ],
};

const ANSWER_CARDS = [
    "a pet rock named 'Gary'", "my extensive collection of toenail clippings", "the concept of personal hygiene",
    "a five-hour interpretive dance", "my imaginary friend, Bartholomew", "the meaning of life, according to a squirrel",
    "their ex's entire family tree", "a spontaneous trip to the moon", "a heated debate about flat earth theories",
    "my obsession with competitive eating", "the ghost of my past relationship", "a really bad karaoke performance",
    "a detailed PowerPoint presentation on my insecurities", "my unique collection of lint", "a crying baby simulator",
    "the sound of a kazoo orchestra", "their unedited diary entries from middle school", "a sock puppet show",
    "the recipe for my grandma's infamous fruitcake", "a detailed analysis of my dream journal", "their pet goldfish",
    "a surprise visit from a mariachi band", "a 30-page manifesto on why cats are superior to dogs", "my crippling fear of ducks",
    "a life-sized cardboard cutout of myself", "the exact moment I realized I was allergic to gluten", "my collection of antique staplers",
    "a detailed explanation of quantum physics", "their personal alien abduction story", "a single, lonely potato",
    "the dark secrets of my childhood treehouse", "my profound love for glitter", "a dramatic re-enactment of my last dentist visit",
    "a squirrel dressed as a tiny chef", "my secret talent for burping the alphabet backwards", "a miniature alpaca farm",
    "the lingering smell of old gym socks", "my deep philosophical thoughts on cheese", "a very aggressive snail",
    "a conspiracy theory involving garden gnomes", "my questionable fashion choices from the 90s", "the universal language of farts",
    "a spontaneous opera performance", "my collection of rare, vintage sporks", "the exact number of hairs on my head",
    "a detailed map of my internal organs", "my uncanny ability to predict the weather with my knee", "a flock of angry pigeons",
    "my personal brand of awkward silence", "a very confused mime", "the entire script of a terrible B-movie",
    "a deep dive into celebrity gossip from 2003", "my most embarrassing childhood story, told in interpretive dance", "a rogue tumbleweed",
    "my collection of forgotten dreams", "a perpetually confused stare", "the subtle art of passive aggression",
    "a commitment to never folding laundry again", "the true identity of Santa Claus", "a never-ending loop of elevator music",
    "my inability to tell left from right", "a lifetime supply of questionable advice", "the secret to perfect toast",
    "a spontaneous act of kindness towards a pigeon", "my irrational fear of balloons", "a detailed budget of my impulse purchases",
    "the surprising history of garden hoses", "my unwavering dedication to napping", "a profound appreciation for mundane objects",
    "my personal record for most consecutive sneezes", "a hidden talent for imitating barnyard animals", "the shocking truth about socks",
    "my unwavering belief in unicorns", "a detailed analysis of my coffee preferences", "the silent language of eyebrow raises",
    "a deep philosophical debate about condiments", "my surprising expertise in competitive napping", "the existential dread of a Monday morning",
    "a profound understanding of cat psychology", "my secret stash of emergency chocolate", "the art of graceful awkwardness",
    "a detailed plan for world domination (starting with my pantry)", "my most cherished collection of bottle caps", "the subtle nuances of air guitar",
    "a profound appreciation for bad puns", "my impressive ability to procrastinate creatively", "the surprising comfort of mismatched socks",
    "a detailed study of dust bunnies", "my personal philosophy on the optimal way to butter toast", "the quiet rebellion of untamed hair",
    "a deep contemplation of the universe while staring at a ceiling fan", "my unwavering commitment to collecting novelty erasers", "the dramatic flair of a well-timed eye-roll"
];

// Component for displaying temporary messages/modals instead of alerts
const MessageModal = ({ message, onClose }) => {
    if (!message) return null;
    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-lg p-6 shadow-xl max-w-sm w-full text-center">
                <p className="text-xl font-semibold mb-4 text-gray-800">{message}</p>
                <button
                    onClick={onClose}
                    className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-6 rounded-full transition duration-300 transform hover:scale-105"
                >
                    OK
                </button>
            </div>
        </div>
    );
};

const RED_FLAG_TITLES = {
    0: { title: "Soulmate Status/ Certified Lover", description: "Congratulations! You are pure relationship gold. Smooth sailing, good vibes, and probably a waiting list for dates." },
    10: { title: "Almost Perfect / Just a Vibe", description: "A few tiny quirks, but nothing a little communication can't handle. You're basically custom-ordered for a healthy partnership." },
    20: { title: "The Minor Tune-Up / Good to Go, Mostly", description: "You've got some minor glitches, but nothing that derails the whole operation. A little self-awareness and effort, and you're golden." },
    30: { title: "The 'Work In Progress' / Relationship Project", description: "Okay, we see some areas for improvement. You're still worth the effort, but don't forget the manual for emotional intelligence." },
    40: { title: "Proceed with Caution / Yellow Light", description: "The warning signs are starting to flash. There's potential, but someone's gonna need a hard hat and a lot of patience to build something solid here." },
    50: { title: "Flip a Coin/ Mixed Bag", description: "You're a true enigma. Half a dream, half a nightmare. This relationship is gonna keep everyone guessing. Strap in!" },
    60: { title: "Danger Zone / Enter at Your Own Risk", description: "The red flags are starting to outnumber the green ones. This connection might come with more drama than a reality TV show." },
    70: { title: "High Maintenance, Low Reward / The Tempting Trap", description: "Oh, you're charismatic alright, but behind that charm, there are some serious issues lurking. It's like a beautiful car with no brakes." },
    80: { title: "Warning Sign Warrior / Full Steam Ahead to Chaos", description: "You're not just waving red flags; you've got a whole parade of them. This relationship needs a professional, or an escape plan." },
    90: { title: "RUN! / Evacuate Immediately", description: "This isn't dating; it's an extreme sport. Every interaction is a potential landmine. Get out. Now." },
    100: { title: "The Walking Red Flag / Relationship Kryptonite", description: "You are the embodiment of every cautionary tale. A master class in 'what not to do.' Stay far, far away for everyone's safety." }
};

// Rename KRIYA_ADS to POPUP_ADS
const POPUP_ADS = [
    {
        title: "Kriya Botanicals - Nourish Your Roots!",
        image: "https://scontent-mia3-1.xx.fbcdn.net/v/t39.30808-6/473014145_1022672133212383_7455427243637921771_n.jpg?_nc_cat=111&ccb=1-7&_nc_sid=cc71e4&_nc_ohc=xDgn4zdguJIQ7kNvwGKd2EK&_nc_oc=AdlKTmywN5e6dNvjc-yH_7PTAt12RPrNgqKMqLvT7dmfz0T0vyMRNeNVJwVjvxCroYw&_nc_zt=23&_nc_ht=scontent-mia3-1.xx&_nc_gid=1g-FyU6O3VDFy1DvpHwpIQ&oh=00_AfO1xUz6MGVrsMqNnc2j7dDQbCK7_5gHvU5ea6G2gWpHUA&oe=685F16E9", // Example placeholder
        description: "Experience the power of nature with Kriya Botanicals natural hair products. Hydrate, strengthen, and shineâ€”naturally! Try our best-selling leave-in conditioner today."
    },
    {
        title: "Kriya Botanicals - Curls That Wow!",
        image: "https://scontent-mia3-1.xx.fbcdn.net/v/t39.30808-6/486493494_10161139828952543_6107835121073011412_n.jpg?_nc_cat=106&ccb=1-7&_nc_sid=833d8c&_nc_ohc=Tq5GlDW58V0Q7kNvwE80yCw&_nc_oc=Adli1caSZyzmwB3bfqDa3P5OWq2NZ6vLvvpDSdiuat-BjOWQ5a75T0YB75z8GwuIO0E&_nc_zt=23&_nc_ht=scontent-mia3-1.xx&_nc_gid=4KS3SwcRA5uIpc_UOm_AIw&oh=00_AfOFchd-nFXsXQjFWqG4XUI5190aEb7tKHfCFMRQEYBgog&oe=685F49EA",
        description: "Unleash your curls with Kriya Botanicals. Our botanical blends define, detangle, and dazzle. Because your hair deserves the best!"
    },
    {
        title: "Kriya Botanicals - Pure, Plant-Powered Beauty!",
        image: "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=600&q=80https://scontent-mia3-3.xx.fbcdn.net/v/t39.30808-6/470148513_18467575291005150_3673084829007527660_n.jpg?_nc_cat=108&ccb=1-7&_nc_sid=127cfc&_nc_ohc=UmuJoiF97esQ7kNvwHfL6Nj&_nc_oc=AdnjIysxZXN4E67SZMhW5Rh9rhW5fo5Q775XdrvXbhmCkAf0odlt5EZ3Nz12_L-VlmI&_nc_zt=23&_nc_ht=scontent-mia3-3.xx&_nc_gid=OavS8Zp0_-Z8vECDAJM5XQ&oh=00_AfMi6EA_D2JZLiWekipC6JII-fplbOXpldIS2Fdmb-NpFQ&oe=685F272E",
        description: "Go green with your routine! Kriya Botanicals uses only the purest plant ingredients for healthy, happy hair. Discover your new favorite today."
    }
];

// Main App Component
function App() {
    const [userId, setUserId] = useState(null);
    const [currentPage, setCurrentPage] = useState('home');
    const [previousPage, setPreviousPage] = useState('home');
    const [gameId, setGameId] = useState(null);
    const [gameData, setGameData] = useState(null);
    const [message, setMessage] = useState(null);
    const [isAuthReady, setIsAuthReady] = useState(false);
    const [isLoadingImage, setIsLoadingImage] = useState(false); // For host loading state
    const [playerName, setPlayerName] = useState(''); // New state for player nickname
    const [isGenerating, setIsGenerating] = useState(false);
    const [user, setUser] = useState(null);

    // Define isHost at the top level so it's available in all handlers
    const isHost = gameData && gameData.hostId === userId;

    // Move generateRedFlagQuestion to App scope so it's available everywhere
    const generateRedFlagQuestion = async () => {
        try {
            const response = await fetch('http://localhost:3001/api/generate-red-flag-question', {
                method: 'POST'
            });
            if (!response.ok) throw new Error('Failed to generate question');
            return await response.json();
        } catch (error) {
            console.error('AI backend failed, using fallback question:', error);
            // Fallback question and choices
            return {
                type: 'scenario_vote',
                text: "Your partner insists on bringing their pet iguana to every date. What's your move?",
                choices: {
                    a: "That's a dealbreaker. I'm not sharing my fries with a lizard.",
                    b: "Honestly, that's kind of awesome. Can I get it a tiny hat?",
                    c: "I have so many questions, but I'm here for the chaos.",
                    d: "As long as the iguana doesn't judge my karaoke, we're good."
                },
                pillar: "Relationship Riddles"
            };
        }
    };

    // Auth state listener and initial sign-in logic
    useEffect(() => {
        const SESSION_USER_ID_KEY = 'redFlagsGameTempUserId';

        const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
            const initialAuthToken = process.env.REACT_APP_INITIAL_AUTH_TOKEN;
            if (initialAuthToken) {
                // If an initial auth token is provided (e.g., from the Canvas environment)
                // Use Firebase authentication with the provided token.
                if (!currentUser) {
                    try {
                        await signInWithCustomToken(auth, initialAuthToken);
                    } catch (error) {
                        console.error("Firebase Auth Error with custom token:", error);
                        setMessage(`Authentication failed: ${error.message}. Please refresh.`);
                    }
                }
                // Once authenticated, set the user and their UID from Firebase.
                if (auth.currentUser) {
                    setUserId(auth.currentUser.uid);
                    // Set default player name if not already set, or load from storage
                    if (!playerName) {
                        setPlayerName(`Player ${auth.currentUser.uid.substring(0, 4)}`);
                    }
                }
            } else {
                // If no initial auth token (e.g., in a standalone browser tab for testing)
                // Generate and use a temporary, session-based user ID.
                let tempUserId = sessionStorage.getItem(SESSION_USER_ID_KEY);
                if (!tempUserId) {
                    tempUserId = `guest_${crypto.randomUUID()}`; // Generate a unique ID
                    sessionStorage.setItem(SESSION_USER_ID_KEY, tempUserId);
                    console.log("Generated new session userId:", tempUserId);
                } else {
                    console.log("Reusing session userId:", tempUserId);
                }

                setUserId(tempUserId); // Use the session ID as the userId
                // Set default player name for guest
                if (!playerName) {
                    setPlayerName(`Player ${tempUserId.substring(0, 5)}`);
                }
            }
            setIsAuthReady(true); // Auth listener has completed its initial check or temp ID is set
        });
        return () => unsubscribe();
    }, [playerName]); // Added playerName to dependencies to prevent re-rendering issues

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
            setUser(firebaseUser);
        });
        return () => unsubscribe();
    }, []);

    // Firebase game data listener
    useEffect(() => {
        if (!gameId || !isAuthReady || !userId) return; // Ensure auth and user are ready

        const gameDocRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
        const unsubscribeGame = onSnapshot(gameDocRef, async (docSnap) => {
            if (docSnap.exists()) {
                const newData = { id: docSnap.id, ...docSnap.data() };

                // Use functional update to compare with previous state without causing re-subscription
                setGameData(prevData => {
                    // If a new image judgment prompt was just set by the host, generate the image
                    if (
                        userId === newData.hostId &&
                        newData.status === 'submission' &&
                        newData.prompts[newData.currentRound - 1]?.type === 'image_judgment' &&
                        !newData.prompts[newData.currentRound - 1]?.imageUrl &&
                        (prevData?.currentRound !== newData.currentRound || prevData?.status !== 'submission')
                    ) {
                        setIsLoadingImage(true);
                        generateImageForPrompt(newData.prompts[newData.currentRound - 1].ai_prompt_description).then(imageUrl => {
                            updateDoc(gameDocRef, {
                                [`prompts.${newData.currentRound - 1}.imageUrl`]: imageUrl,
                                isLoadingImage: false
                            });
                            setIsLoadingImage(false);
                        });
                    }
                    return newData; // Update the state with the new data
                });

                // Navigate based on game status
                if (newData.status === 'submission' || newData.status === 'voting') {
                    setCurrentPage('game');
                } else if (newData.status === 'round_end') {
                    setCurrentPage('leaderboard');
                } else if (newData.status === 'game_over') {
                    setCurrentPage('winner');
                } else if (newData.status === 'lobby') {
                    setCurrentPage('lobby');
                }
            } else {
                console.error("Game document does not exist!");
                setGameId(null);
                setGameData(null);
                setCurrentPage('home');
                showMessage("The game you were in no longer exists.");
            }
        });

        return () => unsubscribeGame();
    }, [gameId, isAuthReady, userId]); // Correct dependency array, removed gameData

    // Utility to generate image (placeholder)
    const generateImageForPrompt = async (promptDescription) => {
        console.log("Starting image generation for:", promptDescription);
        // This is a placeholder. In a real app, you would call your AI image model here.
        // For now, we return a static placeholder image URL after a short delay.
        await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate network delay
        const placeholderUrl = `https://via.placeholder.com/400x300.png?text=${encodeURIComponent(promptDescription.substring(0, 30))}`;
        console.log("Generated image URL:", placeholderUrl);
        return placeholderUrl;
    };

    const showMessage = (msg) => {
        setMessage(msg);
    };

    const handleNextRound = async () => {
        if (!isHost) return;

        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            const nextRound = gameData.currentRound + 1;

            // Always transition to winner if at or past maxRounds
            if (nextRound > gameData.maxRounds || gameData.currentRound >= gameData.maxRounds) {
                await updateDoc(gameRef, { status: 'winner' });
                return;
            }

            let finalPrompts = [];
            let finalMaxRounds = 10;
            let promptHistory = gameData.promptHistory || [];

            let finalPrompt;
            if (gameData.category === "Red Flag or Relationship Goal") {
                // For this mode, prompts are pre-generated. Just get the next one.
                finalPrompt = gameData.prompts[nextRound - 1];
                if (!finalPrompt) {
                    console.error("Could not find the next prompt for Red Flag mode.");
                    await updateDoc(gameRef, { status: 'winner' });
                    return;
                }
            } else {
                // For other modes, generate a new prompt dynamically
                const promptCardsForCategory = PROMPT_CARDS[gameData.category] || [];
                if (promptCardsForCategory.length === 0) {
                    showMessage("No prompts available for this category.");
                    return;
                }
                finalPrompt = getRandomPrompt(promptCardsForCategory, promptHistory);
                promptHistory = [...promptHistory, finalPrompt.text];
                finalMaxRounds = Math.min(10, promptCardsForCategory.length);
            }

            // Reset player states for the new round
            const updatedPlayers = {};
            let availableCards = [...ANSWER_CARDS]; // For replenishment logic (only used for Red Flag/Looks Like)

            for (const pId in gameData.players) {
                const player = gameData.players[pId];
                let currentHand = player.hand || [];
                // Only replenish hands for Red Flag or Relationship Goal and Looks Like
                if (gameData.category === "Red Flag or Relationship Goal" || gameData.category === "Looks Like") {
                    const cardsNeeded = 10 - currentHand.length;
                    if (cardsNeeded > 0) {
                        const shuffledDeck = shuffleArray([...availableCards]);
                        const newCards = shuffledDeck.slice(0, cardsNeeded);
                        currentHand.push(...newCards);
                    }
                }
                // For all other categories, do NOT replenish hand
                updatedPlayers[pId] = {
                    ...player,
                    hand: currentHand,
                    hasSubmitted: false,
                    hasVoted: false
                };
            }

            const updatePayload = {
                status: 'submission',
                currentRound: nextRound,
                players: updatedPlayers,
                submittedAnswers: {},
                playerChoices: {},
                votes: {},
                roundResults: deleteField(),
                currentWinningSubmission: null,
                isLoadingImage: finalPrompt.type === 'image_judgment'
            };

            // Only update prompts if they are not pre-loaded
            if (gameData.category !== "Red Flag or Relationship Goal") {
                updatePayload.prompts = [...gameData.prompts, finalPrompt];
                updatePayload.promptHistory = promptHistory;
            }

            await updateDoc(gameRef, updatePayload);
        } catch (error) {
            console.error("Error advancing round:", error);
            showMessage(`Error advancing round: ${error.message}`);
        }
    };

    const handlePlayAgain = async () => {
        if (gameData.hostId !== userId) {
            showMessage("Only the host can start a new game.");
            return;
        }

        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            
            // Reset player states
            const resetPlayers = { ...gameData.players };
            for (const pId in resetPlayers) {
                resetPlayers[pId] = {
                    ...resetPlayers[pId],
                    score: 0,
                    hand: [],
                    hasSubmitted: false,
                    hasVoted: false,
                    totalHearts: 0,
                    totalRedFlags: 0,
                };
            }

            await updateDoc(gameRef, {
                status: 'lobby',
                currentRound: 0,
                maxRounds: 10,
                prompts: [],
                promptHistory: [],
                submittedAnswers: {},
                playerChoices: {},
                votes: {},
                roundResults: deleteField(),
                currentWinningSubmission: null,
                isLoadingImage: false,
                players: resetPlayers,
                category: Object.keys(PROMPT_CARDS)[0] // Reset to default category
            });
            // The onSnapshot listener will automatically handle the page transition to 'lobby'
        } catch (error) {
            console.error("Error restarting game:", error);
            showMessage("Failed to restart the game.");
        }
    };

    // Room code display (top left)
    const renderRoomCode = () => {
        if (gameData && gameData.gameCode) {
            return (
                <div className="fixed top-2 left-2 bg-gray-800 text-white px-4 py-2 rounded shadow-lg z-50">
                    Room Code: {gameData.gameCode}
                </div>
            );
        }
        return null;
    };

    // Render logic based on currentPage
    const renderPage = () => {
        switch (currentPage) {
            case 'home':
                return <HomePage setCurrentPage={setCurrentPage} setGameId={setGameId} userId={userId} showMessage={showMessage} playerName={playerName} setPlayerName={setPlayerName} />;
            case 'lobby':
                return <LobbyPage gameData={gameData} gameId={gameId} userId={userId} showMessage={showMessage} generateRedFlagQuestion={generateRedFlagQuestion} isHost={isHost} />;
            case 'game':
                return <GamePage gameData={gameData} gameId={gameId} userId={userId} showMessage={showMessage} isLoadingImage={isLoadingImage} />; // Pass isLoadingImage
            case 'leaderboard':
                return <LeaderboardPage gameData={gameData} userId={userId} gameId={gameId} showMessage={showMessage} handleNextRound={handleNextRound} />; // Adjusted to use updated handleNextRound via prop
            case 'winner':
                return <WinnerPage gameData={gameData} userId={userId} onPlayAgain={handlePlayAgain} showMessage={showMessage} />; // Pass showMessage for modal
            default:
                return <HomePage setCurrentPage={setCurrentPage} setGameId={setGameId} userId={userId} showMessage={showMessage} playerName={playerName} setPlayerName={setPlayerName} />;
        }
    };

    if (!isAuthReady) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-800 via-pink-600 to-red-500 text-white font-inter">
                <p className="text-3xl animate-pulse">Loading game...</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-800 via-pink-600 to-red-500 text-white font-inter flex flex-col items-center justify-center p-4 relative">
            {renderRoomCode()}
            {/* Game/category name at the top, except on Player Profile */}
            {currentPage !== 'profile' && (
                <h1 className="text-5xl md:text-6xl font-extrabold text-center mb-8 drop-shadow-lg leading-tight">
                    {gameData && gameData.category ? gameData.category : 'Red Flags & Relationship Goals'}
                </h1>
            )}
            {renderPage()}
        </div>
    );
}

// HomePage Component
const HomePage = ({ setCurrentPage, setGameId, userId, showMessage, playerName, setPlayerName }) => {
    const [joinCode, setJoinCode] = useState('');

    const handleCreateGame = async () => {
        if (!playerName.trim()) {
            showMessage("Please enter your nickname.");
            return;
        }
        try {
            const gameCode = Math.random().toString(36).substring(2, 8).toUpperCase();
            const initialPlayers = {};
            initialPlayers[userId] = {
                id: userId,
                name: playerName.trim(),
                score: 0,
                isHost: true,
                hand: [],
                hasSubmitted: false,
                hasVoted: false,
                totalRedFlags: 0,
                totalHearts: 0
            };
            const newGameRef = await addDoc(collection(db, `artifacts/${appId}/public/data/games`), {
                hostId: userId,
                category: "Can We Talk?",
                mode: "canWeTalk",
                currentRound: 0,
                rounds: [],
                status: 'lobby',
                players: initialPlayers,
                gameCode: gameCode,
                createdAt: serverTimestamp()
            });
            setGameId(newGameRef.id);
            setCurrentPage('lobby');
            showMessage(`Game created! Share code: ${gameCode}`);
        } catch (error) {
            console.error("Error creating game:", error);
            showMessage("Failed to create game. Please try again.");
        }
    };

    const handleJoinGame = async () => {
        if (!playerName.trim()) {
            showMessage("Please enter your nickname.");
            return;
        }
        if (!joinCode) {
            showMessage("Please enter a game code.");
            return;
        }

        try {
            console.log(`Attempting to query Firestore for collection: artifacts/${appId}/public/data/games with gameCode: ${joinCode.toUpperCase()}`); // More detailed debugging log
            const q = query(collection(db, `artifacts/${appId}/public/data/games`), where("gameCode", "==", joinCode.toUpperCase()));
            const querySnapshot = await getDocs(q);
            console.log("Query Snapshot empty:", querySnapshot.empty); // Debugging log

            if (!querySnapshot.empty) {
                const gameDoc = querySnapshot.docs[0];
                const gameData = gameDoc.data();
                const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameDoc.id);

                // Allow joining at any status
                if (gameData.players[userId]) {
                    showMessage("You are already in this game.");
                    // Route to the correct page based on game status
                    if (gameData.status === 'submission' || gameData.status === 'voting') {
                        setCurrentPage('game');
                    } else if (gameData.status === 'round_end') {
                        setCurrentPage('leaderboard');
                    } else if (gameData.status === 'game_over') {
                        setCurrentPage('winner');
                    } else {
                        setCurrentPage('lobby');
                    }
                    setGameId(gameDoc.id);
                    return;
                }

                // Give initial cards
                const playerHand = shuffleArray([...ANSWER_CARDS]).slice(0, 10);

                await updateDoc(gameRef, {
                    [`players.${userId}`]: {
                        id: userId, // Added unique ID for the player object
                        name: playerName.trim(), // Use custom player name
                        score: 0,
                        isHost: false,
                        hand: playerHand,
                        hasSubmitted: false,
                        hasVoted: false,
                        totalRedFlags: 0,
                        totalHearts: 0
                    }
                });
                setGameId(gameDoc.id);
                // Route to the correct page based on game status
                if (gameData.status === 'submission' || gameData.status === 'voting') {
                    setCurrentPage('game');
                } else if (gameData.status === 'round_end') {
                    setCurrentPage('leaderboard');
                } else if (gameData.status === 'game_over') {
                    setCurrentPage('winner');
                } else {
                    setCurrentPage('lobby');
                }
                showMessage(`Joined game ${joinCode}!`);
            } else {
                showMessage("Invalid game code.");
            }
        } catch (error) {
            console.error("Error joining game:", error);
            showMessage("Failed to join game. Please try again.");
        }
    };

    return (
        <div className="text-center">
            <input
                type="text"
                placeholder="Enter your nickname"
                value={playerName}
                onChange={(e) => setPlayerName(e.target.value)}
                className="w-full p-3 rounded-xl bg-white bg-opacity-20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 mb-4 text-center"
                maxLength="20" // Limit nickname length
            />
            <button
                onClick={handleCreateGame}
                className="w-full mb-4 bg-gradient-to-r from-blue-500 to-teal-400 hover:from-blue-600 hover:to-teal-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
            >
                Create New Game
            </button>
            <div className="mb-6">
                <p className="text-gray-200 mb-2">OR</p>
                <input
                    type="text"
                    placeholder="Enter Game Code"
                    value={joinCode}
                    onChange={(e) => setJoinCode(e.target.value)}
                    className="w-full p-3 rounded-xl bg-white bg-opacity-20 text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 mb-3 text-center uppercase"
                    maxLength="6"
                />
                <button
                    onClick={handleJoinGame}
                    className="w-full bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                >
                    Join Game
                </button>
            </div>
            <p className="text-sm text-gray-300 mt-4">
                Minimum 2 players needed to start a game.
            </p>
        </div>
    );
};

// LobbyPage Component
const LobbyPage = ({ gameData, gameId, userId, showMessage, generateRedFlagQuestion, isHost }) => {
    // Move useState to the top
    const [isGenerating, setIsGenerating] = useState(false);

    // Add a more specific check to ensure gameData and its nested properties are loaded
    if (!gameData || !gameData.hostId || !gameData.players) {
        return <p className="text-center text-xl text-gray-200 animate-pulse">Loading lobby...</p>;
    }

    const playersInLobby = Object.values(gameData.players).sort((a, b) => a.name.localeCompare(b.name));

    const handleCategoryChange = async (e) => {
        if (!isHost) return;
        const newCategory = e.target.value;
        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            await updateDoc(gameRef, { category: newCategory });
        } catch (error) {
            console.error("Error updating category:", error);
            showMessage("Failed to update category.");
        }
    };

    const handleStartGame = async () => {
        if (!isHost) return;

        const playersInGame = Object.values(gameData.players);
        if (playersInGame.length < 1) { // Allows solo testing
            showMessage("You need at least 1 player to start the game.");
            return;
        }

        setIsGenerating(true);
        try {
            const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
            
            let finalPrompts = [];
            let finalMaxRounds = 10;
            let promptHistory = [];

            if (gameData.category === "Red Flag or Relationship Goal") {
                for (let i = 0; i < 10; i++) {
                    const newPrompt = await generateRedFlagQuestion();
                    finalPrompts.push(newPrompt);
                }
                finalMaxRounds = finalPrompts.length;
            } else {
                const promptCardsForCategory = PROMPT_CARDS[gameData.category] || [];
                if (promptCardsForCategory.length === 0) {
                    showMessage("No prompts available for this category.");
                    setIsGenerating(false);
                    return;
                }
                const initialPrompt = getRandomPrompt(promptCardsForCategory, []);
                finalPrompts.push(initialPrompt);
                finalMaxRounds = Math.min(10, promptCardsForCategory.length);
            }

            finalPrompts.forEach(p => promptHistory.push(p.text));
            
            // Distribute unique hands to all players
            let availableCards = [...ANSWER_CARDS];
            const updatedPlayers = { ...gameData.players };

            for (const pId in updatedPlayers) {
                const hand = [];
                // Ensure players always have 10 cards at the start of the game
                for (let i = 0; i < 10; i++) {
                    if (availableCards.length === 0) {
                        // If pool runs low, replenish it with all ANSWER_CARDS shuffled
                        availableCards = shuffleArray([...ANSWER_CARDS]);
                        console.warn("Answer card pool replenished by reshuffling all cards during initial deal.");
                    }
                    const cardIndex = Math.floor(Math.random() * availableCards.length);
                    hand.push(availableCards.splice(cardIndex, 1)[0]);
                }
                updatedPlayers[pId].hand = hand;
                updatedPlayers[pId].hasSubmitted = false;
                updatedPlayers[pId].hasVoted = false;
            }

            await updateDoc(gameRef, {
                status: 'submission',
                currentRound: 1,
                maxRounds: finalMaxRounds,
                prompts: finalPrompts,
                promptHistory: promptHistory,
                players: updatedPlayers,
                playerChoices: {},
                submittedAnswers: {},
                votes: {},
                isLoadingImage: finalPrompts[0]?.type === 'image_judgment'
            });

        } catch (error) {
            console.error("Error starting game:", error);
            showMessage("An error occurred while starting the game.");
        } finally {
            setIsGenerating(false);
        }
    };

    return (
        <div className="text-center p-4">
            <h2 className="text-4xl font-bold mb-4 text-white">Game Lobby</h2>
            <p className="text-xl text-gray-200 mb-6">
                Game Code: <span className="font-bold text-yellow-300">{gameData.gameCode}</span>
            </p>
            <div className="text-lg text-gray-200 mb-2 flex items-center justify-center">
                <span className="mr-2">Category:</span>
                {isHost ? (
                    <select
                        value={gameData.category}
                        onChange={handleCategoryChange}
                        className="p-2 rounded-lg bg-white bg-opacity-20 text-white font-semibold focus:outline-none focus:ring-2 focus:ring-blue-400"
                    >
                        {Object.keys(PROMPT_CARDS).map(cat => (
                            <option key={cat} value={cat} className="text-black">{cat}</option>
                        ))}
                    </select>
                ) : (
                    <span className="font-semibold text-pink-200">{gameData.category}</span>
                )}
            </div>
            <p className="text-lg text-gray-200 mb-4">Players Connected:</p>
            <ul className="list-disc list-inside text-left mx-auto max-w-xs mb-8 text-gray-100">
                {playersInLobby.map(player => (
                    <li key={player.id} className="py-1">
                        {player.name} {player.isHost && "(Host)"}
                    </li>
                ))}
            </ul>
            {isHost && (
                <div className="mt-6">
                    {isGenerating ? (
                        <div className="text-center">
                            <p className="text-2xl text-white animate-pulse">We are generating hilarious and problematic scenarios...</p>
                            <p className="text-gray-400 mt-2">This might take a moment.</p>
                        </div>
                    ) : (
                        <button
                            onClick={handleStartGame}
                            disabled={!isHost || playersInLobby.length < 1}
                            className="w-full bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                        >
                            Start Game
                        </button>
                    )}
                </div>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Waiting for host to start the game!</p>
            )}
        </div>
    );
};

// GamePage Component
const GamePage = ({ gameData, gameId, userId, showMessage, isLoadingImage }) => { // Pass isLoadingImage
    if (!gameData || !gameData.prompts || gameData.prompts.length === 0) {
        return <p className="text-center text-xl text-gray-200">Waiting for game to start...</p>;
    }
    const currentPrompt = gameData.prompts[gameData.currentRound - 1];

    if (!currentPrompt) {
        return <p className="text-center text-xl text-gray-200">Loading prompt data...</p>;
    }

    return <GameRound gameData={gameData} gameId={gameId} userId={userId} showMessage={showMessage} isLoadingImage={isLoadingImage} />; // Pass isLoadingImage
};

const GamePrompt = ({ prompt, gameData, isLoadingImage }) => { // Receive isLoadingImage
    const [imageUrl, setImageUrl] = useState(prompt.imageUrl);
    const [isGeneratingImage, setIsGeneratingImage] = useState(false);

    useEffect(() => {
        if (
            prompt.category === 'Looks Like' &&
            !imageUrl &&
            prompt.pillar
        ) {
            setIsGeneratingImage(true);
            fetch('/api/generate-relationship-image', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    pillar: prompt.pillar,
                    description: prompt.ai_prompt_description // send the full scenario
                })
            })
            .then(res => res.json())
            .then(data => {
                setImageUrl(data.imageUrl);
                setIsGeneratingImage(false);
                // Optionally: update Firestore with the new imageUrl for this prompt
            })
            .catch(() => setIsGeneratingImage(false));
        }
    }, [prompt, imageUrl]);

    if (!prompt) return null;

    // Show loading message if it's an image prompt AND host is currently generating it
    if ((prompt.type === 'image_judgment' && isLoadingImage) || isGeneratingImage) { // Check local loading state
        return (
            <div className="text-center p-4">
                <h2 className="text-3xl font-bold mb-4 text-white animate-pulse">Generating AI Image...</h2>
                <p className="text-xl text-gray-200">The host is creating a new visual prompt. Please wait.</p>
            </div>
        );
    }

    // Guard: if prompt.text is undefined, show a fallback or handle image-only prompts
    let promptContent;
    if (prompt.category === 'Looks Like') {
        // For Looks Like, do NOT display any prompt text or description
        promptContent = null;
    } else if (typeof prompt.text === 'string') {
        promptContent = <p className="text-3xl md:text-4xl font-extrabold text-white leading-tight"
            dangerouslySetInnerHTML={{ __html: prompt.text.replace(/__________________/g, '<span class=\"text-pink-300 underline\">BLANK</span>') }}
        />;
    } else if ((prompt.type === 'image_judgment' || prompt.category === 'Looks Like') && prompt.ai_prompt_description) {
        promptContent = <p className="text-2xl font-bold text-white mb-2">{prompt.ai_prompt_description}</p>;
    } else {
        promptContent = <p className="text-2xl font-bold text-white mb-2">No prompt text available.</p>;
    }

    return (
        <div className="text-center bg-white bg-opacity-20 rounded-xl p-4 md:p-6 shadow-xl w-full max-w-2xl border border-white border-opacity-30 mb-6">
            <p className="text-xl font-bold text-yellow-200 mb-2">{prompt.pillar}</p>
            {promptContent}
            {(prompt.type === 'image_judgment' || prompt.category === 'Looks Like') && imageUrl && (
                <img src={imageUrl} alt={prompt.ai_prompt_description || prompt.pillar} className="mt-4 rounded-lg shadow-lg mx-auto w-full max-w-md h-auto object-contain" />
            )}
            <p className="mt-4 text-lg text-gray-200">
                Round {gameData.currentRound} of {gameData.maxRounds}
            </p>
        </div>
    );
};

const GameRound = ({ gameData, gameId, userId, showMessage, isLoadingImage }) => { // Receive isLoadingImage
    // Move all hooks to the top
    const [selectedCard, setSelectedCard] = useState(null);
    const [customAnswer, setCustomAnswer] = useState('');
    const [nominationData, setNominationData] = useState({ nominatedPlayerId: '', justification: '' });
    const [selectedVote, setSelectedVote] = useState(null);
    const [selectedChoice, setSelectedChoice] = useState(null); // For scenario_vote
    const [isWritingCustom, setIsWritingCustom] = useState(false);
    const [customScenarioAnswer, setCustomScenarioAnswer] = useState('');

    const gameRef = doc(db, `artifacts/${appId}/public/data/games`, gameId);
    const currentPlayer = gameData.players[userId];
    const { players: allPlayers, currentRound, status, prompts, submittedAnswers: submittedAnswersObj } = gameData;
    const currentPrompt = prompts[currentRound - 1]; // Access the correct prompt based on round
    const isHost = gameData.hostId === userId;

    const submittedAnswers = Object.values(submittedAnswersObj || {}).sort((a, b) => (a.submittedAt?.seconds || 0) - (b.submittedAt?.seconds || 0));
    const playersInGame = Object.values(allPlayers || {}).sort((a, b) => a.name.localeCompare(b.name));
    const votersCount = playersInGame.filter(p => p.hasVoted).length;

    // These must be defined before any useEffect or code that uses them
    const playersWhoShouldSubmit = playersInGame;
    const submittedCount = currentPrompt.type === 'scenario_vote'
        ? Object.keys(gameData.playerChoices || {}).length
        : submittedAnswers.length;
    const allPlayersSubmitted = submittedCount === playersWhoShouldSubmit.length;

    const shuffledPlayersToVoteOn = useMemo(() => {
        if (!playersInGame) return [];
        // Shuffle a copy of the array to avoid mutation and keep it stable
        return shuffleArray([...playersInGame.filter(p => p.id !== userId)]);
    }, [playersInGame, userId]);

    useEffect(() => {
        setSelectedCard(null);
        setCustomAnswer('');
        setNominationData({ nominatedPlayerId: '', justification: '' });
        setSelectedVote(null);
        setSelectedChoice(null);
        setIsWritingCustom(false);
        setCustomScenarioAnswer('');
    }, [gameData.status, gameData.currentRound]);

    useEffect(() => {
        // Only trigger if current phase is 'submission' and all relevant players have submitted
        if (isHost && gameData.status === 'submission' && allPlayersSubmitted && playersWhoShouldSubmit.length > 0) {
            updateDoc(gameRef, { status: 'voting' }).catch(e => console.error("Error setting status to voting:", e));
        }
    }, [allPlayersSubmitted, playersWhoShouldSubmit.length, isHost, gameData.status, gameRef]);

    useEffect(() => {
        // Only trigger if we are in the voting phase and all active players have voted
        const votersCount = playersInGame.filter(p => p.hasVoted).length;
        const allPlayersVoted = votersCount === playersInGame.length;
        if (isHost && gameData.status === 'voting' && allPlayersVoted && playersInGame.length > 0) {
            const updates = {};
            let roundResults = {}; // For scenario_vote display
            if (currentPrompt.type === 'scenario_vote') {
                playersInGame.forEach(player => {
                    const choiceKey = gameData.playerChoices[player.id];
                    if (typeof choiceKey === 'undefined') return; // Skip players who didn't submit
                    const votesOnPlayer = gameData.votes[player.id] || {};
                    const hearts = (votesOnPlayer.hearts || []).length;
                    const redFlags = (votesOnPlayer.redFlags || []).length;
                    const roundScore = hearts - redFlags;
                    updates[`players.${player.id}.score`] = increment(roundScore);
                    updates[`players.${player.id}.totalHearts`] = increment(hearts);
                    updates[`players.${player.id}.totalRedFlags`] = increment(redFlags);
                    roundResults[player.id] = {
                        choiceKey,
                        hearts,
                        redFlags
                    };
                });
            } else {
                // Existing scoring logic for other modes
                let maxVotes = 0;
                let winningSubmissions = [];
                submittedAnswers.forEach(sub => {
                    if (sub.votes > maxVotes) {
                        maxVotes = sub.votes;
                        winningSubmissions = [sub];
                    } else if (sub.votes === maxVotes) {
                        winningSubmissions.push(sub);
                    }
                });
                if (maxVotes > 0) {
                    winningSubmissions.forEach(sub => {
                        updates[`players.${sub.playerId}.score`] = increment(1);
                    });
                }
                // Store winning submission IDs for retrieval in LeaderboardPage
                updates.currentWinningSubmission = maxVotes > 0 ? winningSubmissions.map(s => s.id) : null;
            }
            updates.status = 'round_end';
            if (currentPrompt.type === 'scenario_vote') {
                updates.roundResults = roundResults;
            }
            updateDoc(gameRef, updates).catch(e => console.error("Error ending round:", e));
        }
    }, [isHost, gameData.status, playersInGame, currentPrompt, gameData.playerChoices, gameData.votes, submittedAnswers]);

    // Guard: if currentPlayer is undefined, show loading or null
    if (!currentPlayer) {
        return <p className="text-center text-xl text-gray-200">Joining game... Please wait.</p>;
    }

    const handleSelectCard = (card) => {
        setSelectedCard(card);
    };

    const handleCustomAnswerChange = (e) => {
        setCustomAnswer(e.target.value);
    };

    const handleNominationChange = (field, value) => {
        setNominationData(prev => ({ ...prev, [field]: value }));
    };

    const handleSubmitAnswer = async () => {
        if (currentPlayer.hasSubmitted) {
            showMessage("You have already submitted an answer for this round.");
            return;
        }

        // If it's an image judgment prompt and the image hasn't loaded yet, prevent submission
        if (currentPrompt.type === 'image_judgment' && !currentPrompt.imageUrl && isLoadingImage) {
            showMessage("Please wait for the image to finish generating before submitting.");
            return;
        }

        let answerContent = '';
        // For fill and image_judgment, answer is a card or custom text
        if (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') {
            if (selectedCard === null) {
                showMessage("Please select a card or type a custom answer.");
                return;
            }
            if (selectedCard === "BLANK_CARD_SELECTED") {
                if (!customAnswer.trim()) {
                    showMessage("Please type your custom answer.");
                    return;
                }
                answerContent = customAnswer.trim();
            } else {
                answerContent = selectedCard;
            }
        } else if (currentPrompt.type === 'thisSoundsLike') {
            if (!nominationData.nominatedPlayerId || !nominationData.justification.trim()) {
                showMessage("Please select a player and provide a justification.");
                return;
            }
            answerContent = {
                nominatedPlayerId: nominationData.nominatedPlayerId,
                justification: nominationData.justification.trim()
            };
        } else {
            showMessage("Invalid prompt type.");
            return;
        }

        try {
            const submissionId = generateId();
            const updates = {
                [`players.${userId}.hasSubmitted`]: true,
                // Only remove the card if it was selected from the hand and not a custom answer
                [`players.${userId}.hand`]: (currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && selectedCard !== "BLANK_CARD_SELECTED"
                    ? currentPlayer.hand.filter(card => card !== selectedCard)
                    : currentPlayer.hand,
                [`submittedAnswers.${submissionId}`]: {
                    id: submissionId,
                    playerId: userId,
                    answer: answerContent,
                    votes: 0,
                    voters: [],
                    submittedAt: serverTimestamp() // Add timestamp for stable sorting
                }
            };

            await updateDoc(gameRef, updates);
            // --- Analytics: increment roundsPlayed, answersSubmitted, customAnswersSubmitted ---
            const playerStatsRef = doc(db, `artifacts/${appId}/public/data/playerStats`, userId);
            await updateDoc(playerStatsRef, {
                roundsPlayed: increment(1),
                answersSubmitted: increment(1),
                ...(selectedCard === "BLANK_CARD_SELECTED" ? { customAnswersSubmitted: increment(1) } : {})
            }).catch(async (err) => {
                if (err.code === 'not-found') {
                    await setDoc(playerStatsRef, {
                        roundsPlayed: 1,
                        answersSubmitted: 1,
                        customAnswersSubmitted: selectedCard === "BLANK_CARD_SELECTED" ? 1 : 0,
                        wins: 0,
                        gamesPlayed: 0,
                        votesReceived: 0,
                        heartsReceived: 0,
                        redFlagsReceived: 0,
                        name: currentPlayer.name
                    });
                }
            });
            // --- End analytics ---
            showMessage("Answer submitted!");
        } catch (error) {
            console.error("Error submitting answer:", error);
            showMessage("Failed to submit answer. Please try again.");
        }
    };

    const handleSubmitChoice = async () => {
        const answer = isWritingCustom ? customScenarioAnswer.trim() : selectedChoice;
        if (!answer) {
            showMessage("Please select a choice or write your own answer.");
            return;
        }

        try {
            const updates = {
                [`players.${userId}.hasSubmitted`]: true,
                [`playerChoices.${userId}`]: answer
            };
            await updateDoc(gameRef, updates);
            showMessage("Your choice has been submitted!");
        } catch (error) {
            console.error("Error submitting choice:", error);
            showMessage("Failed to submit choice. Please try again.");
        }
    };

    const handleScenarioVote = async (targetPlayerId, voteType) => {
        if (currentPlayer.hasVoted) {
            showMessage("You have already submitted your votes for this round.");
            return;
        }

        // Prevent voting for self for scenario_vote as well
        if (targetPlayerId === userId) {
            showMessage("You cannot vote for your own take.");
            return;
        }

        const votePath = `votes.${targetPlayerId}.${voteType}`;
        try {
            await updateDoc(gameRef, {
                [votePath]: arrayUnion(userId)
            });
            // --- Analytics: increment heartsReceived or redFlagsReceived for the answer's owner ---
            const playerStatsRef = doc(db, `artifacts/${appId}/public/data/playerStats`, targetPlayerId);
            if (voteType === 'hearts') {
                await updateDoc(playerStatsRef, { heartsReceived: increment(1) }).catch(async (err) => {
                    if (err.code === 'not-found') {
                        await setDoc(playerStatsRef, { heartsReceived: 1, name: gameData.players[targetPlayerId]?.name || '' });
                    }
                });
            } else if (voteType === 'redFlags') {
                await updateDoc(playerStatsRef, { redFlagsReceived: increment(1) }).catch(async (err) => {
                    if (err.code === 'not-found') {
                        await setDoc(playerStatsRef, { redFlagsReceived: 1, name: gameData.players[targetPlayerId]?.name || '' });
                    }
                });
            }
            // --- End analytics ---
            showMessage(`${voteType === 'hearts' ? 'Heart' : 'Red Flag'} given!`);
        } catch (error) {
            console.error("Error casting scenario vote:", error);
            showMessage("Failed to cast vote.");
        }
    };

    const handleLockInVotes = async () => {
        if (currentPlayer.hasVoted) return;
        try {
            await updateDoc(gameRef, { [`players.${userId}.hasVoted`]: true });
            showMessage("Votes locked in!");
        } catch (error) {
            console.error("Error locking in votes:", error);
            showMessage("Failed to lock in votes.");
        }
    };

    const handleVote = async (submissionId) => {
        if (currentPlayer.hasVoted) {
            showMessage("You've already voted this round.");
            return;
        }
        if (selectedVote) {
            showMessage("You can only vote for one answer.");
            return;
        }

        const submission = submittedAnswersObj[submissionId]; // Use submittedAnswersObj directly
        if (!submission) return;

        // Prevent voting for oneself in applicable modes
        if (submission.playerId === userId) {
            showMessage("You cannot vote for your own answer.");
            return;
        }

        setSelectedVote(submissionId); // Optimistically update UI

        try {
            await updateDoc(gameRef, {
                [`submittedAnswers.${submissionId}.votes`]: increment(1),
                [`submittedAnswers.${submissionId}.voters`]: arrayUnion(userId),
                [`players.${userId}.hasVoted`]: true,
                [`votes.${submissionId}`]: arrayUnion(userId) // General vote tracking
            });
            // --- Analytics: increment votesReceived for the answer's owner ---
            const playerStatsRef = doc(db, `artifacts/${appId}/public/data/playerStats`, submission.playerId);
            await updateDoc(playerStatsRef, { votesReceived: increment(1) }).catch(async (err) => {
                if (err.code === 'not-found') {
                    await setDoc(playerStatsRef, { votesReceived: 1, name: gameData.players[submission.playerId]?.name || '' });
                }
            });
            // --- End analytics ---
            showMessage("Vote submitted!");
        } catch (error) {
            console.error("Error submitting vote:", error);
            setSelectedVote(null); // Revert on error
            showMessage("Failed to submit vote. Please try again.");
        }
    };

    const playerHand = currentPlayer?.hand || [];

    return (
        <div className="w-full max-w-4xl mx-auto p-4 flex flex-col items-center justify-center">
            <GamePrompt prompt={currentPrompt} gameData={gameData} isLoadingImage={isLoadingImage} /> {/* Pass isLoadingImage */}

            {gameData.status === 'submission' && (
                currentPrompt.type === 'scenario_vote' ? (
                    <div className="w-full text-center">
                        {!currentPlayer.hasSubmitted ? (
                            <>
                                <h3 className="text-2xl font-bold text-white mb-4">What's your take?</h3>
                                {isWritingCustom ? (
                                    <div className="mb-4">
                                        <textarea
                                            value={customScenarioAnswer}
                                            onChange={(e) => setCustomScenarioAnswer(e.target.value)}
                                            className="w-full p-3 rounded-lg bg-white text-gray-900 h-24"
                                            placeholder="Make it spicy..."
                                        ></textarea>
                                        <button onClick={() => setIsWritingCustom(false)} className="text-cyan-300 hover:text-cyan-400 mt-2">Back to choices</button>
                                    </div>
                                ) : (
                                    <>
                                        <div className="space-y-3 mb-6">
                                            {Object.entries(currentPrompt.choices).sort((a, b) => a[0].localeCompare(b[0])).map(([key, value]) => (
                                                <button
                                                    key={key}
                                                    onClick={() => setSelectedChoice(key)}
                                                    className={`w-full text-left p-4 rounded-xl shadow-md transition transform ${
                                                        selectedChoice === key ? 'bg-blue-400 scale-105 ring-4 ring-blue-500 text-white' : 'bg-white hover:bg-gray-100 text-gray-900'
                                                    }`}
                                                >
                                                    <span className="font-bold uppercase mr-3">{key})</span>{value}
                                                </button>
                                            ))}
                                        </div>
                                        <button onClick={() => { setIsWritingCustom(true); setSelectedChoice(null); }} className="w-full bg-gray-700 hover:bg-gray-600 text-white font-bold py-3 px-6 rounded-full shadow-lg mb-4">
                                            ...or write your own answer
                                        </button>
                                    </>
                                )}
                                
                                <button
                                    onClick={handleSubmitChoice}
                                    disabled={isWritingCustom ? !customScenarioAnswer.trim() : !selectedChoice}
                                    className="w-full bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 disabled:opacity-50"
                                >
                                    Submit Choice
                                </button>
                            </>
                        ) : (
                            <p className="text-2xl text-gray-200 animate-pulse">Waiting for other players to submit their take...</p>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Submitted: {submittedCount} / {playersWhoShouldSubmit.length}
                        </p>
                    </div>
                ) : (
                    <div className="w-full max-w-lg mx-auto">
                        {!currentPlayer.hasSubmitted ? (
                            <>
                                {currentPrompt.type !== 'thisSoundsLike' && (
                                    <>
                                        <h3 className="text-xl font-bold text-white mb-4">Your Hand:</h3>
                                        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 mb-6">
                                            {playerHand.map((card, index) => (
                                                <button
                                                    key={card + index}
                                                    onClick={() => handleSelectCard(card)}
                                                    className={`p-3 rounded-lg shadow-md h-32 text-sm text-gray-900 transition-transform transform hover:scale-105 ${selectedCard === card ? 'bg-cyan-300 ring-4 ring-cyan-500' : 'bg-white hover:bg-gray-100'}`}
                                                >
                                                    {card}
                                                </button>
                                            ))}
                                            {(currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && (
                                                <button
                                                    onClick={() => handleSelectCard("BLANK_CARD_SELECTED")}
                                                    className={`p-3 rounded-lg shadow-md h-32 text-sm transition-transform transform hover:scale-105 ${selectedCard === "BLANK_CARD_SELECTED" ? 'bg-yellow-300 ring-4 ring-yellow-500 text-gray-900' : 'bg-gray-200 hover:bg-gray-300 text-gray-900'}`}
                                                >
                                                    Write your own...
                                                </button>
                                            )}
                                        </div>
                                    </>
                                )}
                                {currentPrompt.type === 'thisSoundsLike' && (
                                    <div className="mb-4">
                                        <h3 className="text-xl font-bold text-white mb-2">1. Nominate a Player</h3>
                                        <select
                                            value={nominationData.nominatedPlayerId}
                                            onChange={(e) => setNominationData(prev => ({ ...prev, nominatedPlayerId: e.target.value }))}
                                            className="w-full p-3 rounded-lg bg-white text-gray-900"
                                        >
                                            <option value="">Select a player...</option>
                                            {playersInGame.filter(p => p.id !== userId).map(p => (
                                                <option key={p.id} value={p.id}>{p.name}</option>
                                            ))}
                                        </select>
                                        <h3 className="text-xl font-bold text-white mb-2 mt-4">2. Justify your nomination</h3>
                                        <textarea
                                            value={nominationData.justification}
                                            onChange={(e) => setNominationData(prev => ({ ...prev, justification: e.target.value }))}
                                            placeholder="Why does this sound like them?"
                                            className="w-full p-3 rounded-lg bg-white text-gray-900 h-24"
                                        ></textarea>
                                    </div>
                                )}
                                {selectedCard === "BLANK_CARD_SELECTED" && currentPrompt.type !== 'thisSoundsLike' && (
                                    <div className="mb-4">
                                        <h3 className="text-xl font-bold text-white mb-2">Write Your Custom Answer</h3>
                                        <textarea
                                            value={customAnswer}
                                            onChange={(e) => setCustomAnswer(e.target.value)}
                                            className="w-full p-3 rounded-lg bg-white text-gray-900 h-24"
                                            placeholder="Make it good!"
                                        ></textarea>
                                    </div>
                                )}
                                <button
                                    onClick={handleSubmitAnswer}
                                    className="w-full bg-gradient-to-r from-teal-500 to-cyan-600 hover:from-teal-600 hover:to-cyan-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                                    disabled={((currentPrompt.type === 'fill' || currentPrompt.type === 'image_judgment') && (!selectedCard || (selectedCard === "BLANK_CARD_SELECTED" && !customAnswer.trim()))) || (currentPrompt.type === 'thisSoundsLike' && (!nominationData.nominatedPlayerId || !nominationData.justification.trim()))}
                                >
                                    Submit Answer
                                </button>
                            </>
                        ) : (
                            <p className="text-2xl text-gray-200 animate-pulse">Waiting for other players to submit...</p>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Submitted: {submittedCount} / {playersWhoShouldSubmit.length}
                        </p>
                    </div>
                )
            )}

            {gameData.status === 'voting' && (
                currentPrompt.type === 'scenario_vote' ? (
                    <div className="w-full text-center">
                        <h3 className="text-2xl font-bold text-white mb-4">Judge Their Takes!</h3>
                        <p className="text-gray-300 mb-6">Give a â¤ï¸ for a relationship goal or a ðŸš© for a red flag.</p>
                        <div className="space-y-4 mb-6">
                            {shuffledPlayersToVoteOn.map(player => { // Use shuffled players
                                const choiceValue = gameData.playerChoices[player.id];
                                const choiceText = choiceValue ? (currentPrompt.choices[choiceValue] || choiceValue) : "No choice submitted.";
                                const hearts = gameData.votes?.[player.id]?.hearts || [];
                                const redFlags = gameData.votes?.[player.id]?.redFlags || [];
                                const hasVotedHeart = hearts.includes(userId);
                                const hasVotedFlag = redFlags.includes(userId);

                                return (
                                    <div key={player.id} className="bg-white bg-opacity-20 rounded-xl p-4 text-left">
                                        <p className="text-gray-200 mb-3 text-xl italic">"{choiceText}"</p>
                                        <div className="flex justify-end items-center space-x-3">
                                            <button
                                                onClick={() => handleScenarioVote(player.id, 'hearts')}
                                                disabled={hasVotedHeart || hasVotedFlag || currentPlayer.hasVoted}
                                                className={`px-4 py-2 rounded-full font-bold text-2xl transition transform hover:scale-110 disabled:opacity-50 ${hasVotedHeart ? 'bg-pink-400' : 'bg-gray-300 hover:bg-pink-300'}`}
                                            >
                                                â¤ï¸
                                            </button>
                                            <button
                                                onClick={() => handleScenarioVote(player.id, 'redFlags')}
                                                disabled={hasVotedHeart || hasVotedFlag || currentPlayer.hasVoted}
                                                className={`px-4 py-2 rounded-full font-bold text-2xl transition transform hover:scale-110 disabled:opacity-50 ${hasVotedFlag ? 'bg-red-500' : 'bg-gray-300 hover:bg-red-400'}`}
                                            >
                                                ðŸš©
                                            </button>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                        {!currentPlayer.hasVoted && (
                            <button onClick={handleLockInVotes} className="w-full bg-gradient-to-r from-green-500 to-lime-400 text-white font-bold py-4 px-8 rounded-full shadow-lg">
                                Lock In Your Votes
                            </button>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Voted: {votersCount} / {playersInGame.length}
                        </p>
                    </div>
                ) : (
                    <div className="w-full text-center">
                        <h3 className="text-2xl font-bold text-white mb-4">Vote for your favorite!</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                            {submittedAnswers.map(sub => (
                                <div key={sub.id} className="relative bg-white bg-opacity-20 rounded-xl p-4 flex flex-col justify-between">
                                    <p className="text-white text-lg font-semibold mb-4">"
                                        {typeof sub.answer === 'object' && sub.answer !== null ? sub.answer.justification : sub.answer}
                                    "</p>
                                    <div className="text-right">
                                        <button
                                            onClick={() => handleVote(sub.id)}
                                            disabled={currentPlayer.hasVoted || sub.playerId === userId}
                                            className={`bg-gradient-to-r from-pink-500 to-orange-500 hover:from-pink-600 hover:to-orange-600 text-white font-bold py-2 px-4 rounded-full shadow-lg transition duration-300 disabled:opacity-50 ${sub.playerId === userId ? 'cursor-not-allowed bg-gray-400 hover:from-gray-400 hover:to-gray-400' : ''}`}
                                            title={sub.playerId === userId ? "You can't vote for your own answer" : "Vote for this answer"}
                                        >
                                            {sub.playerId === userId ? "Your Answer" : "Vote"}
                                        </button>
                                        {gameData.votes[sub.id] && <span className="ml-4 text-lg font-bold text-white">{(gameData.votes[sub.id] || []).length}</span>}
                                    </div>
                                </div>
                            ))}
                        </div>
                        {!currentPlayer.hasVoted && (
                            <p className="text-lg text-gray-300">Choose the funniest or most fitting answer!</p>
                        )}
                        <p className="text-gray-300 mt-4 text-lg">
                            Voted: {votersCount} / {playersInGame.length}
                        </p>
                    </div>
                )
            )}
        </div>
    );
};

// LeaderboardPage Component
const LeaderboardPage = ({ gameData, userId, gameId, showMessage, handleNextRound }) => {
    const isHost = gameData.hostId === userId;
    const players = Object.values(gameData.players).sort((a, b) => b.score - a.score);
    const currentPrompt = gameData.prompts[gameData.currentRound - 1]; // Access the correct prompt

    const votersCount = players.filter(p => p.hasVoted).length;

    const winningSubmissions = (gameData.currentWinningSubmission || []).map(id => gameData.submittedAnswers[id]).filter(Boolean);
    const winnerNames = winningSubmissions.map(sub => gameData.players[sub.playerId]?.name).join(', ');

    return (
        <div className="text-center p-4">
            <h2 className="text-4xl font-bold text-white mb-6 animate-fade-in-down">Round {gameData.currentRound} Results</h2>
            {/* Show the current question/prompt at the top */}
            {currentPrompt && (
                <div className="mb-8 bg-white bg-opacity-20 rounded-xl p-4 max-w-2xl mx-auto">
                    <p className="text-lg md:text-2xl font-bold text-yellow-200 mb-2">Question:</p>
                    <p className="text-xl md:text-2xl text-white">{currentPrompt.text}</p>
                </div>
            )}

            {currentPrompt.type === 'scenario_vote' && gameData.roundResults ? (
                 <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 mb-8 w-full max-w-2xl mx-auto">
                    <h3 className="text-2xl font-bold text-cyan-300 mb-4">How did everyone do?</h3>
                    <div className="space-y-4">
                        {players.map(player => {
                            const result = gameData.roundResults[player.id];
                            if (!result) return null;
                            const choiceValue = result.choiceKey;
                            const choiceText = currentPrompt.choices[choiceValue] || choiceValue;
                            return (
                                <div key={player.id} className="text-left p-4 bg-gray-900 bg-opacity-50 rounded-lg">
                                    <p className="font-bold text-white text-xl">{player.name}'s take:</p>
                                    <p className="text-gray-300 italic my-2">"{choiceText}"</p>
                                    <div className="flex justify-end items-center space-x-4 text-xl">
                                        <span className="text-pink-400 font-bold">â¤ï¸ {result.hearts}</span>
                                        <span className="text-red-500 font-bold">ðŸš© {result.redFlags}</span>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>
            ) : (
                <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 mb-8 w-full max-w-2xl mx-auto">
                    <h3 className="text-2xl font-bold text-cyan-300 mb-2">Winning Answer{winningSubmissions.length > 1 ? 's' : ''}</h3>
                    {winningSubmissions.length > 0 ? (
                        <>
                            {winningSubmissions.map(sub => (
                                <p key={sub.id} className="text-white text-xl font-semibold bg-gray-900 bg-opacity-50 rounded-md p-4 my-2">"
                                    {typeof sub.answer === 'object' && sub.answer !== null ? sub.answer.justification : sub.answer}
                                "</p>
                            ))}
                            <p className="text-gray-300 mt-2 text-lg">Submitted by: <span className="font-bold text-white">{winnerNames}</span> (+1 point)</p>
                        </>
                    ) : (
                        <p className="text-white text-xl font-semibold">No winning answer this round. Everyone gets to keep their dignity.</p>
                    )}
                </div>
            )}


            <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 w-full max-w-md mx-auto">
                <h3 className="text-3xl font-bold text-white mb-4">Leaderboard</h3>
                {gameData.category === "Red Flag or Relationship Goal" ? (
                    <ul className="space-y-3 mb-8">
                        {players.map((player, index) => (
                            <li key={player.id} className={`flex items-center justify-between p-4 rounded-xl shadow-md text-white ${
                                index === 0 ? 'bg-yellow-400 bg-opacity-50' : 'bg-white bg-opacity-30'
                            }`}>
                                <span className="text-lg font-semibold">{player.name}</span>
                                <div className="flex items-center space-x-4 text-lg">
                                    <span className="text-pink-400 font-bold">â¤ï¸ {player.totalHearts || 0}</span>
                                    <span className="text-red-500 font-bold">ðŸš© {player.totalRedFlags || 0}</span>
                                </div>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <ul className="space-y-3 mb-8">
                        {players.map((player, index) => (
                            <li key={player.id} className={`flex items-center justify-between p-4 rounded-xl shadow-md ${
                                index === 0 ? 'bg-yellow-300 text-gray-900 font-bold text-xl' : 'bg-white bg-opacity-30 text-white'
                            }`}>
                                <div className="flex items-center space-x-4">
                                    <span className="text-lg font-bold">{index + 1}.</span>
                                    <span className="text-lg">{player.name}</span>
                                </div>
                                <span className="text-lg font-bold">{player.score} pts</span>
                            </li>
                        ))}
                    </ul>
                )}
                {isHost && (
                    <button
                        onClick={handleNextRound}
                        className="w-full bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                    >
                        {gameData.currentRound < gameData.maxRounds ? 'Next Round' : 'Show Final Results'}
                    </button>
                )}
                {!isHost && (
                    <p className="text-xl text-gray-300 italic">Waiting for host to continue...</p>
                )}
            </div>
        </div>
    );
};

// WinnerPage Component
const WinnerPage = ({ gameData, userId, onPlayAgain, showMessage }) => {
    // Add ad logic here
    const [showAd, setShowAd] = useState(true);
    const adIndex = 0; // Always show the first ad, or randomize if you want
    const [analyticsUpdated, setAnalyticsUpdated] = useState(false);

    const handleCloseAd = async () => {
        setShowAd(false);
        // Analytics: Only the host should update analytics
        if (gameData.hostId === userId && !analyticsUpdated) {
            try {
                // Increment global games played
                const statsRef = doc(db, `artifacts/${appId}/public/data/analytics`, 'globalStats');
                await updateDoc(statsRef, { gamesPlayed: increment(1) }).catch(async (err) => {
                    if (err.code === 'not-found') {
                        await setDoc(statsRef, { gamesPlayed: 1 });
                    }
                });
                // Increment gamesPlayed and set lastPlayed for all players
                for (const player of Object.values(gameData.players)) {
                    const playerStatsRef = doc(db, `artifacts/${appId}/public/data/playerStats`, player.id);
                    await updateDoc(playerStatsRef, { gamesPlayed: increment(1), lastPlayed: serverTimestamp() }).catch(async (err) => {
                        if (err.code === 'not-found') {
                            await setDoc(playerStatsRef, { gamesPlayed: 1, lastPlayed: serverTimestamp(), name: player.name });
                        }
                    });
                }
                // Increment wins for the winner(s)
                let winners = [];
                if (gameData.category === "Red Flag or Relationship Goal") {
                    const players = Object.values(gameData.players);
                    let minRatio = Math.min(...players.map(p => ((p.totalRedFlags || 0) / (((p.totalHearts || 0) + (p.totalRedFlags || 0)) || 1))));
                    winners = players.filter(p => ((p.totalRedFlags || 0) / (((p.totalHearts || 0) + (p.totalRedFlags || 0)) || 1)) === minRatio);
                } else {
                    const players = Object.values(gameData.players);
                    let maxScore = Math.max(...players.map(p => p.score || 0));
                    winners = players.filter(p => (p.score || 0) === maxScore);
                }
                for (const winner of winners) {
                    const playerStatsRef = doc(db, `artifacts/${appId}/public/data/playerStats`, winner.id);
                    await updateDoc(playerStatsRef, { wins: increment(1) }).catch(async (err) => {
                        if (err.code === 'not-found') {
                            await setDoc(playerStatsRef, { wins: 1, name: winner.name });
                        }
                    });
                }
                setAnalyticsUpdated(true);
            } catch (err) {
                console.error('Error updating analytics:', err);
            }
        }
    };

    // Add a guard clause for gameData
    if (!gameData || !gameData.players) {
        return <div className="text-center p-4 text-white">Loading final results...</div>;
    }

    const players = Object.values(gameData.players).sort((a, b) => {
        if (gameData.category === "Red Flag or Relationship Goal") {
             const aRatio = ((a.totalRedFlags || 0) / ((a.totalHearts || 0) + (a.totalRedFlags || 0) || 1));
             const bRatio = ((b.totalRedFlags || 0) / ((b.totalHearts || 0) + (b.totalRedFlags || 0) || 1));
             return aRatio - bRatio; // Sort by lowest red flag ratio
        }
        return (b.score || 0) - (a.score || 0); // Default score sort
    });
    
    const isHost = gameData.hostId === userId;

    const handleDeleteGame = async () => {
        showMessage("Are you sure you want to delete this game? This action cannot be undone.", async () => {
            try {
                if (gameData.id) {
                    await deleteDoc(doc(db, `artifacts/${appId}/public/data/games`, gameData.id));
                    onPlayAgain(); // Go back to home page after deletion
                } else {
                    console.error("Game ID is undefined, cannot delete.");
                    showMessage("Failed to delete game: Game ID missing.");
                }
            } catch (error) {
                console.error("Error deleting game:", error);
                showMessage("Failed to delete game. Please try again.");
            }
        });
    };

    const getPlayerTitleAndDescription = (player) => {
        if(gameData.category !== "Red Flag or Relationship Goal") {
            return { title: `Score: ${player.score}`, description: "Played a different game mode." };
        }

        const { totalHearts, totalRedFlags } = player;
        const totalVotes = (totalHearts || 0) + (totalRedFlags || 0);

        if (totalVotes === 0) {
            return { title: "Mystery Card", description: "Not enough votes to get a read. Are you an enigma or just quiet?" };
        }

        const redFlagPercentage = (totalRedFlags / totalVotes) * 100;
        const rating = Math.round(redFlagPercentage / 10) * 10;

        return RED_FLAG_TITLES[rating] || { title: "Calculation Error", description: "Something went wrong here."};
    };

    return (
        <div className="text-center p-4">
            {/* Full-page ad overlay at end of game */}
            {showAd && (
                <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-90">
                    <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-lg w-full flex flex-col items-center relative">
                        <img src={POPUP_ADS[adIndex].image} alt="Kriya Botanicals" className="w-48 h-48 object-cover rounded-xl mb-4 shadow-lg" />
                        <h2 className="text-3xl font-extrabold text-green-700 mb-2">{POPUP_ADS[adIndex].title}</h2>
                        <p className="text-lg text-gray-800 mb-6">{POPUP_ADS[adIndex].description}</p>
                        <button
                            onClick={handleCloseAd}
                            className="bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-3 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                        >
                            Continue
                        </button>
                        <span className="absolute top-2 right-4 text-xs text-gray-400">Sponsored</span>
                    </div>
                </div>
            )}
            {!showAd && (
            <>
            <h2 className="text-5xl font-bold text-yellow-300 mb-4 animate-bounce">Game Over!</h2>
            {players.length > 0 ? <h3 className="text-3xl text-white mb-8">And the winner is... <span className="font-bold text-cyan-400">{players[0].name}!</span></h3> : null}

            <div className="bg-white bg-opacity-20 backdrop-blur-md rounded-xl shadow-lg p-6 w-full max-w-2xl mx-auto">
                <h4 className="text-2xl font-bold text-white mb-4">Final Standings</h4>
                 <ul className="space-y-4">
                    {players.map((player, index) => {
                        const { title, description } = getPlayerTitleAndDescription(player);
                        return (
                            <li key={player.id} className="text-left p-4 bg-gray-900 bg-opacity-50 rounded-lg text-white">
                                <div className="flex justify-between items-center mb-1">
                                    <span className={`text-xl font-bold ${index === 0 ? 'text-yellow-400' : ''}`}>{index + 1}. {player.name}</span>
                                    <span className="text-lg font-semibold text-cyan-300 text-right">{title}</span>
                                </div>
                                <p className="text-sm text-gray-300 italic">"{description}"</p>
                            </li>
                        );
                    })}
                </ul>
            </div>

            {isHost && (
                <>
                    <button
                        onClick={onPlayAgain}
                        className="w-full bg-gradient-to-r from-green-500 to-lime-400 hover:from-green-600 hover:to-lime-500 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105 mb-4"
                    >
                        Play Again!
                    </button>
                    <button
                        onClick={handleDeleteGame}
                        className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-4 px-8 rounded-full shadow-lg transition duration-300 transform hover:scale-105"
                    >
                        Delete This Game
                    </button>
                </>
            )}
            {!isHost && (
                <p className="text-xl text-gray-300 italic">Ask the host to start a new game!</p>
            )}
            </>) }
        </div>
    );
};

// Utility function to shuffle an array (Fisher-Yates)
const shuffleArray = (array) => {
    let currentIndex = array.length, randomIndex;
    while (currentIndex !== 0) {
        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex--;
        [array[currentIndex], array[randomIndex]] = [array[randomIndex], array[currentIndex]];
    }
    return array;
};

// Utility function to get a random prompt that hasn't been played yet
const getRandomPrompt = (promptList, history) => {
    // History should contain the 'text' of previously played prompts
    const availablePrompts = promptList.filter(p => !history.includes(p.text));
    if (availablePrompts.length === 0) {
        console.warn("All prompts for this category have been used. Reshuffling prompts for this round.");
        return promptList[Math.floor(Math.random() * promptList.length)]; // If all used, pick randomly from full list
    }
    const randomIndex = Math.floor(Math.random() * availablePrompts.length);
    return availablePrompts[randomIndex];
};

export default App;
